mapper.select:
select 

from WAQ_CS_ANA_MSTR
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

select *
from WAQ_CS_ANA_MSTR
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and ANA_JOB_ID = #{anaJobId,jdbcType=VARCHAR}
  and ANA_STR_DTM = #{anaStrDtm}

mapper.delete:
delete from WAQ_CS_ANA_MSTR
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
