mapper.select:
  	SELECT * FROM 

  	(SELECT A.*,
  		   (SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.FRS_RQST_USER_ID AND EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND REG_TYP_CD IN ('C','U')) AS FRS_RQST_USER_NM,
    	   (SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.RQST_USER_ID AND EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND REG_TYP_CD IN ('C','U')) AS RQST_USER_NM,
    	   (SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.APRV_USER_ID AND EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND REG_TYP_CD IN ('C','U')) AS APRV_USER_NM 
   	FROM WAM_APP_STWD A)

		AND REG_TYP_CD IN ('C', 'U')

        AND (APP_STWD_LNM LIKE '%' || #{appStwdLnm,jdbcType=VARCHAR} || '%'
        	 OR APP_STWD_PNM LIKE '%' || #{appStwdLnm,jdbcType=VARCHAR} || '%'
        	 OR APP_STWD_LNM LIKE '%' || UPPER(#{appStwdLnm,jdbcType=VARCHAR}) || '%'
        	 OR APP_STWD_PNM LIKE '%' || UPPER(#{appStwdLnm,jdbcType=VARCHAR}) || '%')


        AND APP_STWD_ID = #{AppStwdId,jdbcType=VARCHAR}


        AND UPPER(ENG_MEAN LIKE) '%' || UPPER(#{engMean,jdbcType=VARCHAR}) || '%'


        AND RQST_NO = #{rqstNo,jdbcType=VARCHAR}


        AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}


        AND OBJ_DESCN LIKE '%' || #{objDescn,jdbcType=VARCHAR} || '%'


        AND OBJ_VERS = #{objVers,jdbcType=DECIMAL}


        AND REG_TYP_CD = #{regTypCd,jdbcType=VARCHAR}


        AND FRS_RQST_DTM = #{frsRqstDtm,jdbcType=DATE}


        AND FRS_RQST_USER_ID = #{frsRqstUserId,jdbcType=VARCHAR}


        AND RQST_DTM = #{rqstDtm,jdbcType=DATE}


        AND RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}


        AND APRV_DTM = #{aprvDtm,jdbcType=DATE}


        AND APRV_USER_ID = #{aprvUserId,jdbcType=VARCHAR}


		 AND TO_CHAR(APRV_DTM, 'YYYY-MM-DD') >= #{searchBgnDe} 


		 AND TO_CHAR(APRV_DTM, 'YYYY-MM-DD') <= #{searchEndDe} 


  	ORDER BY APP_STWD_LNM
