mapper.select:
select 

from WAQ_APP_SDITM
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_APP_SDITM
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
    SELECT A.*
	  FROM WAQ_APP_SDITM A

	 WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
	   AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
 	UPDATE WAQ_APP_SDITM SET APP_SDITM_ID = #{appSditmId,jdbcType=VARCHAR} , OBJ_VERS = 1
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
SELECT ((SELECT NVL(MAX(RQST_SNO), 0) FROM WAQ_APP_SDITM WHERE RQST_NO = #{reqmst.rqstNo}) + ROWNUM) AS RQST_SNO , 'I' AS IBS_STATUS , A.APP_SDITM_ID, A.APP_SDITM_LNM, A.APP_SDITM_PNM, A.LNM_CRI_DS, A.PNM_CRI_DS, A.DATA_TYPE, A.DATA_LEN, A.DATA_SCAL , #{reqmst.rqstDcd} AS RQST_DCD , A.OBJ_DESCN, A.OBJ_VERS FROM WAM_APP_SDITM A WHERE A.APP_SDITM_ID IN
#{item.appSditmId}
AND A.REG_TYP_CD IN ('C', 'U')
