mapper.select:
select 

from WAQ_DDL_COL A
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_DDL_COL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

DELETE FROM WAQ_DDL_COL
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
UPDATE WAQ_DDL_COL A
   SET (DDL_TBL_PNM, RQST_DCD) = (
   	   			SELECT B.DDL_TBL_PNM, B.RQST_DCD FROM WAQ_DDL_TBL B
   	   			 WHERE B.RQST_NO = A.RQST_NO
   	   			   AND B.RQST_SNO = A.RQST_SNO 
   )
 WHERE A.RQST_NO = #{rqstNo}
   AND A.RQST_SNO = #{rqstSno}

mapper.insert:
--DDL에 존재하고 PDM에 없는 컬럼을 삭제대상으로 추가한다.... INSERT INTO WAQ_DDL_COL( RQST_NO, RQST_SNO, RQST_DTL_SNO , VRF_CD , DDL_COL_ID, DDL_COL_PNM, DDL_COL_LNM, RQST_DCD, REG_TYP_CD , DDL_TBL_ID, DDL_TBL_PNM, COL_ORD, DATA_TYPE, DATA_LEN, DATA_SCAL, PK_YN, PK_ORD, NONUL_YN, DEFLT_VAL , OBJ_DESCN, OBJ_VERS, FRS_RQST_DTM, FRS_RQST_USER_ID, RQST_DTM, RQST_USER_ID ) SELECT D.RQST_NO, D.RQST_SNO , (SELECT NVL(MAX(RQST_DTL_SNO), 0) FROM WAQ_DDL_COL WHERE RQST_NO = #{rqstNo} AND RQST_SNO = D.RQST_SNO ) + 1 AS RQST_DTL_SNO , '4' AS VRF_CD , C.DDL_COL_ID, C.DDL_COL_PNM, C.DDL_COL_LNM, 'DD' AS RQST_DCD, 'D' AS REG_TYP_CD , B.DDL_TBL_ID, B.DDL_TBL_PNM, C.COL_ORD, C.DATA_TYPE, C.DATA_LEN, C.DATA_SCAL, C.PK_YN, C.PK_ORD, C.NONUL_YN, C.DEFLT_VAL , C.OBJ_DESCN, C.OBJ_VERS, D.FRS_RQST_DTM, D.FRS_RQST_USER_ID, SYSDATETIME, D.RQST_USER_ID FROM WAM_DDL_TBL B JOIN WAQ_DDL_TBL D ON B.DB_SCH_ID = D.DB_SCH_ID AND B.DDL_TBL_PNM = D.DDL_TBL_PNM
JOIN WAM_DDL_COL C ON B.DDL_TBL_ID = C.DDL_TBL_ID WHERE D.RQST_NO = #{rqstNo} AND NOT EXISTS ( SELECT 1 FROM WAQ_DDL_COL X WHERE D.RQST_NO = X.RQST_NO AND D.RQST_SNO = X.RQST_SNO AND C.DDL_COL_PNM = X.DDL_COL_PNM
)

mapper.update:
   --신규 적재 대상 ID 업데이트
   UPDATE WAQ_DDL_COL 
     SET DDL_COL_ID = #{ddlColId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
     AND RQST_DTL_SNO = #{rqstDtlSno}
