mapper.select:
select 

,

from WAQ_DDL_IDX
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_DDL_IDX
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

DELETE FROM WAQ_DDL_IDX
 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
   AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
SELECT MAX(RQST_SNO) AS RQST_SNO
  FROM WAQ_DDL_IDX 
 WHERE RQST_NO     = #{rqstNo, jdbcType=VARCHAR}
   AND DDL_TBL_PNM = #{ddlTblPnm, jdbcType=VARCHAR}
 GROUP BY DDL_TBL_PNM

	-- DDL 인덱스 요청서 상세조회
	  SELECT A.*
    	   , B.DDL_TBL_LNM
      FROM WAQ_DDL_IDX A 
           LEFT JOIN WAM_DDL_TBL B
             ON B.DDL_TBL_ID = A.DDL_TBL_ID
     WHERE A.RQST_NO  = #{rqstNo,jdbcType=VARCHAR}
       AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
       AND A.RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

mapper.delete:
DELETE FROM WAQ_DDL_IDX
 WHERE RQST_NO      = #{rqstNo}
   AND RQST_SNO     = #{rqstSno}
   AND RQST_DTL_SNO = #{rqstDtlSno}

mapper.update:
	UPDATE WAQ_DDL_IDX

	    RVW_STS_CD 	= #{rvwStsCd} , 
		RVW_CONTS 	= #{rvwConts} , 
		APRV_DTM	= SYSDATETIME ,
		APRV_USER_ID = #{aprvUserId} ,

  WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
    AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
    AND RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}
    AND VRF_CD = '1'
    AND NVL(RVW_STS_CD, '_') != '2'

   --신규 적재 대상 ID 업데이트
  UPDATE WAQ_DDL_IDX 
     SET DDL_IDX_ID = #{ddlIdxId,jdbcType=VARCHAR}
	 WHERE RQST_NO      = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO     = #{rqstSno,jdbcType=DECIMAL}
     AND RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

mapper.insert:
--WAM 추가 (WAQ 내용을 추가, REG_TYP_CD 가 신규,변경일 경우) INSERT INTO WAM_DDL_IDX (DDL_IDX_ID
) SELECT DDL_IDX_ID
FROM WAQ_DDL_IDX A WHERE A.RQST_NO = #{rqstNo} AND A.VRF_CD = '1' AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

--WAH이력 적재... INSERT INTO WAH_DDL_IDX ( DDL_IDX_ID , EXP_DTM , STR_DTM
) SELECT A.DDL_IDX_ID AS DDL_IDX_ID , TO_DATE( '9999-12-31', 'YYYY-MM-DD') AS EXP_DTM , SYSDATETIME AS STR_DTM
FROM WAQ_DDL_IDX A WHERE A.RQST_NO = #{rqstNo} AND A.VRF_CD = '1' AND A.RVW_STS_CD = '1'

mapper.select:
--DDL인덱스 변경대상 조회... SELECT A.DDL_IDX_ID , 'I' AS IBS_STATUS , MAX(A.DDL_IDX_PNM) AS DDL_IDX_PNM , MAX(A.DDL_IDX_LNM) AS DDL_IDX_LNM , #{reqmst.rqstDcd, jdbcType=VARCHAR} AS RQST_DCD , MAX(A.DB_CONN_TRG_ID) AS DB_CONN_TRG_ID , MAX(DB.DB_CONN_TRG_PNM) AS DB_CONN_TRG_PNM , MAX(A.DB_SCH_ID) AS DB_SCH_ID , MAX(S.DB_SCH_PNM) AS DB_SCH_PNM , MAX(A.DDL_TBL_ID) AS DDL_TBL_ID , MAX(DD.DDL_TBL_PNM) AS DDL_TBL_PNM , MAX(A.IDX_SPAC_ID) AS IDX_SPAC_ID , MAX(IDX.DB_TBL_SPAC_PNM) AS IDX_SPAC_PNM , MAX(A.PK_IDX_YN) AS PK_IDX_YN , MAX(A.UK_IDX_YN) AS UK_IDX_YN , MAX(A.IDX_TYP_CD) AS IDX_TYP_CD , MAX(A.DDL_IDX_COL_ASM) AS DDL_IDX_COL_ASM , MAX(A.OBJ_DESCN) AS OBJ_DESCN --, LISTAGG(B.DDL_IDX_COL_PNM ||','|| B.SORT_TYP,';') WITHIN GROUP(ORDER BY B.DDL_IDX_COL_ORD) AS DDL_IDX_COL_ASM FROM WAM_DDL_IDX A LEFT JOIN WAM_DDL_IDX_COL B ON B.DDL_IDX_ID = A.DDL_IDX_ID LEFT JOIN WAM_DDL_TBL DD ON A.DDL_TBL_ID = DD.DDL_TBL_ID AND DD.REG_TYP_CD IN ('C', 'U') LEFT JOIN WAA_DB_SCH S ON A.DB_SCH_ID = S.DB_SCH_ID AND S.REG_TYP_CD IN ('C', 'U') AND S.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT JOIN WAA_DB_CONN_TRG DB ON A.DB_CONN_TRG_ID = DB.DB_CONN_TRG_ID AND DB.REG_TYP_CD IN ('C', 'U') AND DB.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT JOIN WAA_TBL_SPAC IDX ON A.IDX_SPAC_ID = IDX.DB_TBL_SPAC_ID AND IDX.REG_TYP_CD IN ('C', 'U') AND IDX.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') WHERE A.DDL_IDX_ID IN
#{item.ddlIdxId}
GROUP BY A.DDL_IDX_ID

mapper.update:
	--DDL 스크립트 업데이트...
	UPDATE WAQ_DDL_IDX
	   SET SCRT_INFO = #{scrtInfo,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
