mapper.select:
select 

from WAQ_DDL_REL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}
  and DDL_REL_PNM = #{ddlRelPnm,jdbcType=VARCHAR}
  and PA_ENTY_PNM = #{paEntyPnm,jdbcType=VARCHAR}
  and PA_ATTR_PNM = #{paAttrPnm,jdbcType=VARCHAR}
  and CH_ENTY_PNM = #{chEntyPnm,jdbcType=VARCHAR}
  and CH_ATTR_PNM = #{chAttrPnm,jdbcType=VARCHAR}

mapper.delete:
delete from WAQ_DDL_REL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}
  and DDL_REL_PNM = #{ddlRelPnm,jdbcType=VARCHAR}
  and PA_ENTY_PNM = #{paEntyPnm,jdbcType=VARCHAR}
  and PA_ATTR_PNM = #{paAttrPnm,jdbcType=VARCHAR}
  and CH_ENTY_PNM = #{chEntyPnm,jdbcType=VARCHAR}
  and CH_ATTR_PNM = #{chAttrPnm,jdbcType=VARCHAR}

DELETE FROM WAQ_DDL_REL
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
--등록유형 업데이트 UPDATE WAQ_DDL_REL A SET A.REG_TYP_CD = (select case when A.RQST_DCD = 'DD' then 'D' else case when MAX(B.DDL_REL_ID) IS NULL then 'C' else 'U' end end from WAM_DDL_REL B inner join WAM_DDL_TBL C on C.DDL_TBL_ID = B.CH_ENTY_ID and C.REG_TYP_CD in ('C','U') INNER JOIN WAQ_DDL_TBL D ON C.DB_SCH_ID = D.DB_SCH_ID AND C.DDL_TBL_PNM = D.DDL_TBL_PNM
AND D.RQST_NO = #{rqstNo} where B.REG_TYP_CD in ('C','U') and B.DDL_REL_PNM = A.DDL_REL_PNM
and D.RQST_NO = A.RQST_NO AND D.RQST_SNO = A.RQST_SNO ) ,A.VRF_CD = '4' WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}

mapper.insert:
--검증쿼리 : 변경사항 없을경우 (DR000) AND A.REG_TYP_CD = 'U' AND EXISTS (SELECT 1 FROM WAM_DDL_TBL D JOIN WAQ_DDL_TBL F ON F.DB_SCH_ID = D.DB_SCH_ID AND F.DDL_TBL_PNM = D.DDL_TBL_PNM
JOIN WAM_DDL_REL E ON D.DDL_TBL_ID = E.CH_ENTY_ID WHERE F.RQST_NO = A.RQST_NO AND F.RQST_SNO = A.RQST_SNO AND A.DDL_REL_PNM = E.DDL_REL_PNM AND A.DDL_REL_LNM = E.DDL_REL_LNM and NVL(A.REL_TYP_CD , '_') = NVL(E.REL_TYP_CD , '_') and NVL(A.CRD_TYP_CD , '_') = NVL(E.CRD_TYP_CD , '_') and NVL(A.PA_OPT_TYP_CD , '_') = NVL(E.PA_OPT_TYP_CD , '_') and NVL(A.PA_SUBJ_ID , '_') = NVL(E.PA_SUBJ_ID, '_') and NVL(A.PA_ENTY_PNM , '_') = NVL((SELECT DDL_TBL_PNM FROM WAM_DDL_TBL WHERE REG_TYP_CD IN ('C', 'U') AND DDL_TBL_ID = E.PA_ENTY_ID) , '_') and NVL(A.PA_ATTR_PNM , '_') = NVL((SELECT DDL_COL_PNM FROM WAM_DDL_COL WHERE REG_TYP_CD IN ('C', 'U') AND DDL_COL_ID = E.PA_ATTR_ID) , '_') and NVL(A.CH_SUBJ_ID , '_') = NVL(E.CH_SUBJ_ID, '_') and NVL(A.CH_ENTY_PNM , '_') = NVL((SELECT DDL_TBL_PNM FROM WAM_DDL_TBL WHERE REG_TYP_CD IN ('C', 'U') AND DDL_TBL_ID = E.CH_ENTY_ID) , '_') and NVL(A.CH_ATTR_PNM , '_') = NVL((SELECT DDL_COL_PNM FROM WAM_DDL_COL WHERE REG_TYP_CD IN ('C', 'U') AND DDL_COL_ID = E.CH_ATTR_ID) , '_')
)

mapper.update:
   --신규 적재 대상 ID 업데이트
   UPDATE WAQ_DDL_REL SET DDL_REL_ID = #{ddlRelId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
     AND RQST_DTL_SNO = #{rqstDtlSno}

mapper.insert:
--DDL에 존재하고 PDM에 없는 관계를 삭제대상으로 추가한다.... INSERT INTO WAQ_DDL_REL( RQST_NO, RQST_SNO, RQST_DTL_SNO, DDL_REL_ID, DDL_REL_PNM, DDL_REL_LNM, RQST_DCD, REG_TYP_CD , REL_TYP_CD, CRD_TYP_CD, PA_OPT_TYP_CD, PA_SUBJ_PNM, PA_ENTY_PNM, PA_ATTR_PNM, CH_SUBJ_PNM, CH_ENTY_PNM, CH_ATTR_PNM , OBJ_DESCN, OBJ_VERS, FRS_RQST_DTM, FRS_RQST_USER_ID, RQST_DTM, RQST_USER_ID ) SELECT D.RQST_NO, D.RQST_SNO , (SELECT NVL(MAX(RQST_DTL_SNO), 0) FROM WAQ_DDL_REL WHERE RQST_NO = #{rqstNo} AND RQST_SNO = D.RQST_SNO ) + ROWNUM AS RQST_DTL_SNO , C.DDL_REL_ID, C.DDL_REL_PNM, C.DDL_REL_LNM, 'DD' AS RQST_DCD, 'D' AS REG_TYP_CD , C.REL_TYP_CD, C.CRD_TYP_CD, C.PA_OPT_TYP_CD, PS.FULL_PATH, PE.DDL_TBL_PNM, PC.DDL_COL_PNM, CS.FULL_PATH, CE.DDL_TBL_PNM, CC.DDL_COL_PNM , C.OBJ_DESCN, C.OBJ_VERS, D.FRS_RQST_DTM, D.FRS_RQST_USER_ID, SYSDATETIME, D.RQST_USER_ID FROM WAM_DDL_TBL B INNER JOIN WAM_PDM_TBL PD ON B.PDM_TBL_ID = PD.PDM_TBL_ID AND PD.REG_TYP_CD IN ('C', 'U') JOIN WAQ_DDL_TBL D ON B.DB_SCH_ID = D.DB_SCH_ID AND B.DDL_TBL_PNM = D.DDL_TBL_PNM
JOIN WAM_DDL_REL C ON B.DDL_TBL_ID = C.CH_ENTY_ID AND PD.SUBJ_ID = C.CH_SUBJ_ID LEFT OUTER JOIN WAA_SUBJ PS ON C.PA_SUBJ_ID = PS.SUBJ_ID AND PS.REG_TYP_CD IN ('C', 'U') AND PS.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_SUBJ CS ON C.CH_SUBJ_ID = CS.SUBJ_ID AND CS.REG_TYP_CD IN ('C', 'U') AND CS.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAM_DDL_TBL PE ON C.PA_ENTY_ID = PE.DDL_TBL_ID AND PE.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAM_DDL_TBL CE ON C.CH_ENTY_ID = CE.DDL_TBL_ID AND CE.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAM_DDL_COL PC ON C.PA_ATTR_ID = PC.DDL_COL_ID AND C.PA_ENTY_ID = PC.DDL_TBL_ID AND PC.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAM_DDL_COL CC ON C.CH_ATTR_ID = CC.DDL_COL_ID AND C.CH_ENTY_ID = CC.DDL_TBL_ID AND CC.REG_TYP_CD IN ('C', 'U') WHERE D.RQST_NO = #{rqstNo} AND NOT EXISTS ( SELECT 1 FROM WAQ_DDL_REL X WHERE D.RQST_NO = X.RQST_NO AND D.RQST_SNO = X.RQST_SNO AND C.DDL_REL_PNM = X.DDL_REL_PNM
)
