mapper.select:
select 

from WAQ_PDM_REL_MST
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}
  and PDM_REL_PNM = #{pdmRelPnm,jdbcType=VARCHAR}
  and PA_TBL_PNM = #{paTblPnm,jdbcType=VARCHAR}
  and PA_COL_PNM = #{paAttrPnm,jdbcType=VARCHAR}
  and CH_TBL_PNM = #{chTblPnm,jdbcType=VARCHAR}
  and CH_COL_PNM = #{chAttrPnm,jdbcType=VARCHAR}

mapper.delete:
DELETE FROM WAQ_PDM_REL_MST
 WHERE RQST_NO      = #{rqstNo,jdbcType=VARCHAR}
   AND RQST_SNO     = #{rqstSno,jdbcType=DECIMAL}
   AND RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

mapper.select:
SELECT
, CASE WHEN A.VRF_CD = '2' THEN '#FF0000' ELSE CASE WHEN A.VRF_CD = '5' THEN '#0000FF' END END AS FontColor , U.USER_NM AS RQST_USER_NM FROM WAQ_PDM_REL_MST A LEFT JOIN WAA_USER U ON A.RQST_USER_ID = U.USER_ID AND U.REG_TYP_CD IN ('C', 'U') AND U.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') WHERE A.RQST_NO = #{rqstNo}
AND A.RQST_SNO = #{rqstSno}
ORDER BY A.RQST_SNO , A.RQST_DTL_SNO , A.PA_FULL_PATH , A.PA_TBL_PNM

select 

from WAQ_PDM_REL_MST
where RQST_NO      = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO     = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

SELECT MAX(RQST_SNO) AS RQST_SNO    
  FROM WAQ_PDM_REL_MST
 WHERE RQST_NO      = #{rqstNo, jdbcType=VARCHAR}
   AND PA_FULL_PATH  = #{paFullPath, jdbcType=VARCHAR}
   AND PA_TBL_PNM  = #{paTblPnm, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM WAQ_PDM_REL_MST
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
	UPDATE WAQ_PDM_REL_MST

	    RVW_STS_CD 	= #{rvwStsCd} , 
		RVW_CONTS 	= #{rvwConts} , 
		APRV_DTM	= SYSDATETIME ,
		APRV_USER_ID = #{aprvUserId} ,

  WHERE RQST_NO   = #{rqstNo,jdbcType=VARCHAR}
    AND RQST_SNO  = #{rqstSno,jdbcType=DECIMAL}
    AND RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}
    AND VRF_CD = '1'
    AND NVL(RVW_STS_CD, '_') != '2'

   --신규 적재 대상 ID 업데이트
   UPDATE WAQ_PDM_REL_MST 
      SET PDM_REL_ID = #{pdmRelId,jdbcType=VARCHAR}
	  WHERE RQST_NO      = #{rqstNo,jdbcType=VARCHAR}
      AND RQST_SNO     = #{rqstSno,jdbcType=DECIMAL}
      AND RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

mapper.select:
SELECT A.*
  FROM WAQ_PDM_REL_MST A
 WHERE A.PDM_REL_PNM = #{pdmRelPnm}
   AND A.PA_FULL_PATH = #{paFullPath}
   AND A.PA_TBL_PNM = #{paTblPnm}
   AND A.PA_COL_PNM = #{paAttrPnm}
   AND A.CH_FULL_PATH = #{chFullPath}
   AND A.CH_TBL_PNM = #{chTblPnm}
   AND A.CH_COL_PNM = #{chAttrPnm}
   AND A.RQST_NO = #{rqstNo}
   AND A.RQST_SNO = #{rqstSno}

SELECT MAX(RQST_SNO) AS RQST_SNO
  FROM WAQ_PDM_REL_MST
 WHERE RQST_NO     = #{rqstNo, jdbcType=VARCHAR}
   AND PDM_REL_PNM = #{pdmRelPnm, jdbcType=VARCHAR}
   AND PA_TBL_PNM = #{paTblPnm, jdbcType=VARCHAR}
 GROUP BY PA_TBL_PNM
