,CASE WHEN NVL(B.ENC_YN,'N') = 'N' THEN NVL(A.ENC_YN, 'N') ELSE NVL(B.ENC_YN,'N') END ENC_YN ,CASE WHEN NVL(B.DUP_YN,'N') = 'N' THEN NVL(A.DUP_YN, 'N') ELSE NVL(B.DUP_YN,'N') END DUP_YN ,A.STND_ASRT ,A.PERS_INFO_CNV_YN ,A.PERS_INFO_GRD ,A.PERS_INFO_YN FROM WAM_SDITM A INNER JOIN WAM_DMN B ON A.DMN_ID = B.DMN_ID -- AND A.INFOTP_ID = B.INFOTP_ID AND A.REG_TYP_CD IN ('C', 'U') AND B.REG_TYP_CD IN ('C', 'U') -- LEFT OUTER JOIN WAA_DMNG_INFOTP_MAP C -- ON A.DMNG_ID = C.DMNG_ID -- AND A.INFOTP_ID = C.INFOTP_ID -- AND A.REG_TYP_CD IN ('C', 'U') -- AND C.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_INFO_TYPE D ON A.INFOTP_ID = D.INFOTP_ID AND D.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN (
SELECT A.DMNG_ID, A.STND_ASRT, A.DMNG_LNM, A.DMNG_PNM, A.DMNG_LVL, A.CD_DMN_YN, A.INFOTP_CHG_CAN_YN, A.UPP_DMNG_ID AS UPP_DMNG_ID, A.UPP_DMNG_LNM AS UPP_DMNG_LNM, A.UPP_DMNG_PNM AS UPP_DMNG_PNM, B.COMM_DTL_CD_NM || '>' || CASE WHEN DMNG_LVL = '0' THEN NVL(DMNG_LNM,'') WHEN DMNG_LVL = '1' THEN NVL(B_DMNG_PATH,'') || NVL(DMNG_LNM,'') WHEN DMNG_LVL = '2' THEN NVL(C_DMNG_PATH,'') || NVL(B_DMNG_PATH,'') || NVL(DMNG_LNM,'') WHEN DMNG_LVL = '3' THEN NVL(D_DMNG_PATH,'') || NVL(C_DMNG_PATH,'') || NVL(B_DMNG_PATH,'') || NVL(DMNG_LNM,'') WHEN DMNG_LVL = '4' THEN NVL(E_DMNG_PATH,'') || NVL(D_DMNG_PATH,'') || NVL(C_DMNG_PATH,'') || NVL(B_DMNG_PATH,'') || NVL(DMNG_LNM,'') END AS FULL_PATH FROM (SELECT A.DMNG_ID, A.DMNG_LNM, A.DMNG_PNM, A.DMNG_LVL, A.CD_DMN_YN, A.INFOTP_CHG_CAN_YN, B.DMNG_ID AS UPP_DMNG_ID, B.DMNG_LNM AS UPP_DMNG_LNM, B.DMNG_PNM AS UPP_DMNG_PNM, CASE WHEN B.DMNG_LNM IS NULL THEN NULL ELSE B.DMNG_LNM || '>' END B_DMNG_PATH, C.DMNG_ID AS UPP_UPP_DMNG_ID, C.DMNG_LNM AS UPP_UPP_DMNG_LNM, C.DMNG_PNM AS UPP_UPP_DMNG_PNM, CASE WHEN C.DMNG_LNM IS NULL THEN NULL ELSE C.DMNG_LNM || '>' END C_DMNG_PATH, D.DMNG_ID AS UPP_UPP_UPP_DMNG_ID, D.DMNG_LNM AS UPP_UPP_UPP_DMNG_LNM, D.DMNG_PNM AS UPP_UPP_UPP_DMNG_PNM, CASE WHEN D.DMNG_LNM IS NULL THEN NULL ELSE D.DMNG_LNM || '>' END D_DMNG_PATH, E.DMNG_ID AS UPP_UPP_UPP_UPP_DMNG_ID, E.DMNG_LNM AS UPP_UPP_UPP_UPP_DMNG_LNM, E.DMNG_PNM AS UPP_UPP_UPP_UPP_DMNG_PNM, CASE WHEN E.DMNG_LNM IS NULL THEN NULL ELSE E.DMNG_LNM || '>' END E_DMNG_PATH, A.STND_ASRT FROM WAA_DMNG A LEFT OUTER JOIN WAA_DMNG B ON A.UPP_DMNG_ID = B.DMNG_ID AND B.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_DMNG C ON B.UPP_DMNG_ID = C.DMNG_ID AND C.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_DMNG D ON C.UPP_DMNG_ID = D.DMNG_ID AND D.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_DMNG E ON D.UPP_DMNG_ID = E.DMNG_ID AND E.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD') WHERE A.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD')) A INNER JOIN (SELECT X.COMM_DTL_CD, X.COMM_DTL_CD_NM FROM WAA_COMM_DCD Z INNER JOIN WAA_COMM_DTL_CD X ON Z.COMM_DCD_ID = X.COMM_DCD_ID AND Z.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD') AND Z.REG_TYP_CD IN ('C', 'U') AND X.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD') AND X.REG_TYP_CD IN ('C', 'U') WHERE Z.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD') AND Z.REG_TYP_CD IN ('C', 'U') AND Z.COMM_DCD = 'STNDASRT') B ON A.STND_ASRT = B.COMM_DTL_CD
) G ON A.DMNG_ID = G.DMNG_ID
)
AND SDITM_ID = #{sditmId,jdbcType=VARCHAR}
AND (SDITM_LNM LIKE '%' || #{sditmLnm,jdbcType=VARCHAR} || '%' OR SDITM_PNM LIKE '%' || #{sditmLnm,jdbcType=VARCHAR} || '%' OR SDITM_LNM LIKE '%' || UPPER(#{sditmLnm,jdbcType=VARCHAR}) || '%' OR SDITM_PNM LIKE '%' || UPPER(#{sditmLnm,jdbcType=VARCHAR}) || '%')
AND (DMN_LNM LIKE '%' || #{dmnLnm,jdbcType=VARCHAR} || '%' OR DMN_PNM LIKE '%' || #{dmnLnm,jdbcType=VARCHAR} || '%' OR DMN_LNM LIKE '%' || UPPER(#{dmnLnm,jdbcType=VARCHAR}) || '%' OR DMN_PNM LIKE '%' || UPPER(#{dmnLnm,jdbcType=VARCHAR}) || '%')
AND LNM_CRI_DS = #{lnmCriDs,jdbcType=VARCHAR}
AND DMN_ID = #{dmnId,jdbcType=VARCHAR}
AND DMNG_ID = #{dmngId,jdbcType=VARCHAR}
AND UPP_DMNG_ID = #{uppDmngId,jdbcType=VARCHAR}
AND INFOTP_ID = #{infotpId,jdbcType=VARCHAR}
AND INFOTP_CHG_YN = #{infotpChgYn,jdbcType=VARCHAR}
AND ENC_YN = #{encYn,jdbcType=VARCHAR}
AND DUP_YN = #{dupYn,jdbcType=VARCHAR}
AND RQST_NO = #{rqstNo,jdbcType=VARCHAR}
AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
AND OBJ_DESCN LIKE '%' || #{objDescn,jdbcType=VARCHAR} || '%'
AND OBJ_VERS = #{objVers,jdbcType=DECIMAL}
AND REG_TYP_CD = #{regTypCd,jdbcType=VARCHAR}
AND FRS_RQST_DTM = #{frsRqstDtm,jdbcType=TIMESTAMP}
AND FRS_RQST_USER_ID = #{frsRqstUserId,jdbcType=VARCHAR}
AND RQST_DTM = #{rqstDtm,jdbcType=TIMESTAMP}
AND RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
AND APRV_DTM = #{aprvDtm,jdbcType=TIMESTAMP}
AND APRV_USER_ID = #{aprvUserId,jdbcType=VARCHAR}
AND DATA_TYPE = #{dataType,jdbcType=VARCHAR}
AND TEST_DATA_CNV_YN = #{testDataCnvYn,jdbcType=VARCHAR}
AND TO_CHAR(APRV_DTM, 'YYYY-MM-DD') >= #{searchBgnDe}
AND TO_CHAR(APRV_DTM, 'YYYY-MM-DD') <= #{searchEndDe}
AND STND_ASRT = #{stndAsrt}
AND PERS_INFO_CNV_YN= #{persInfoCnvYn}
AND PERS_INFO_GRD = #{persInfoGrd}
