,CASE WHEN NVL(B.ENC_YN,'N') = 'N' THEN NVL(A.ENC_YN, 'N') ELSE NVL(B.ENC_YN,'N') END ENC_YN ,CASE WHEN NVL(B.DUP_YN,'N') = 'N' THEN NVL(A.DUP_YN, 'N') ELSE NVL(B.DUP_YN,'N') END DUP_YN , DECODE(A.TRANS_YN,'Y','1','0') TRANS_YN , A.STND_ASRT , A.PERS_INFO_CNV_YN , A.PERS_INFO_GRD , A.PERS_INFO_YN FROM WAH_SDITM A INNER JOIN WAM_DMN B ON A.DMN_ID = B.DMN_ID AND A.REG_TYP_CD IN ('C', 'U') AND A.EXP_DTM = TO_DATE('99991231', 'YYYYMMDD') AND B.REG_TYP_CD IN ('C', 'U') LEFT JOIN WAA_INFO_TYPE D ON A.INFOTP_ID = D.INFOTP_ID AND D.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT JOIN V_WAA_DMNG G ON A.DMNG_ID = G.DMNG_ID LEFT JOIN (SELECT A.OBJ_ID ,LISTAGG(B.ENG_MEAN, '_') WITHIN GROUP (ORDER BY A.WD_SNO) AS ITM_ENG_MEAN FROM WAM_STWD_CNFG A INNER JOIN WAM_STWD B ON A.STWD_ID = B.STWD_ID AND B.REG_TYP_CD IN ('C','U') GROUP BY A.OBJ_ID) I ON A.SDITM_ID = I.OBJ_ID LEFT JOIN (SELECT A.OBJ_ID ,LISTAGG(B.ENG_MEAN, '_') WITHIN GROUP (ORDER BY A.WD_SNO) AS DMN_ENG_MEAN FROM WAM_STWD_CNFG A INNER JOIN WAM_STWD B ON A.STWD_ID = B.STWD_ID AND B.REG_TYP_CD IN ('C','U') GROUP BY A.OBJ_ID ) DD ON A.DMN_ID = DD.OBJ_ID
WHERE A.TRANS_YN = #{transYn,jdbcType=VARCHAR}
)
AND SDITM_ID = #{sditmId,jdbcType=VARCHAR}
AND (SDITM_LNM LIKE '%' || #{sditmLnm,jdbcType=VARCHAR} || '%' OR SDITM_PNM LIKE '%' || #{sditmLnm,jdbcType=VARCHAR} || '%' OR SDITM_LNM LIKE '%' || UPPER(#{sditmLnm,jdbcType=VARCHAR}) || '%' OR SDITM_PNM LIKE '%' || UPPER(#{sditmLnm,jdbcType=VARCHAR}) || '%')
AND (DMN_LNM LIKE '%' || #{dmnLnm,jdbcType=VARCHAR} || '%' OR DMN_PNM LIKE '%' || #{dmnLnm,jdbcType=VARCHAR} || '%' OR DMN_LNM LIKE '%' || UPPER(#{dmnLnm,jdbcType=VARCHAR}) || '%' OR DMN_PNM LIKE '%' || UPPER(#{dmnLnm,jdbcType=VARCHAR}) || '%')
AND LNM_CRI_DS = #{lnmCriDs,jdbcType=VARCHAR}
AND DMN_ID = #{dmnId,jdbcType=VARCHAR}
AND DMNG_ID = #{dmngId,jdbcType=VARCHAR}
AND UPP_DMNG_ID = #{uppDmngId,jdbcType=VARCHAR}
AND INFOTP_ID = #{infotpId,jdbcType=VARCHAR}
