UNION ALL select #{dvRqstNo,jdbcType=VARCHAR}, A.STWD_LNM, A.STWD_PNM, 'W' from WAQ_STWD A where A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
and A.VRF_CD = '1' and A.RQST_DCD = 'CU'
