mapper.select:
select 

from WAQ_SDITM
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

SELECT ((SELECT NVL(MAX(RQST_SNO), 0) FROM WAQ_SDITM WHERE RQST_NO = #{reqmst.rqstNo}) + ROWNUM) AS RQST_SNO , 'I' AS IBS_STATUS , A.SDITM_ID, A.SDITM_LNM, A.SDITM_PNM, A.LNM_CRI_DS, A.PNM_CRI_DS , #{reqmst.rqstDcd} AS RQST_DCD , D.DMNG_LNM , A.DMN_ID, B.DMN_LNM, B.DMN_PNM , A.INFOTP_ID, C.DATA_TYPE, C.DATA_LEN, C.DATA_SCAL, C.INFOTP_LNM, A.INFOTP_CHG_YN, A.ENC_YN, A.DUP_YN , A.OBJ_DESCN, A.OBJ_VERS, A.TEST_DATA_CNV_YN , A.STND_ASRT, A.PERS_INFO_CNV_YN, A.PERS_INFO_GRD , A.PERS_INFO_YN FROM WAM_SDITM A INNER JOIN WAM_DMN B ON A.DMN_ID = B.DMN_ID LEFT JOIN WAA_INFO_TYPE C ON C.EXP_DTM = TO_DATE( '9999-12-31', 'YYYY-MM-DD') AND C.INFOTP_ID = A.INFOTP_ID LEFT JOIN WAA_DMNG D ON D.EXP_DTM = TO_DATE( '9999-12-31', 'YYYY-MM-DD') AND D.DMNG_ID = A.DMNG_ID WHERE A.SDITM_ID IN
#{item.sditmId}
AND A.REG_TYP_CD IN ('C', 'U')

mapper.delete:
DELETE FROM WAQ_SDITM
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

DELETE FROM WAQ_SDITM A
 WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
   AND EXISTS (SELECT 1 FROM WAQ_DMN B
              WHERE B.RQST_NO   = #{rqstNo,jdbcType=VARCHAR}
                AND B.RQST_SNO  = #{rqstSno,jdbcType=DECIMAL}
                AND A.SDITM_LNM = B.DMN_LNM
                AND A.STND_ASRT = B.STND_ASRT
                AND A.RQST_NO   = B.RQST_NO)

mapper.update:
 	UPDATE WAQ_SDITM SET SDITM_ID = #{sditmId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.insert:
--WAM 추가 (WAQ 내용을 추가, REG_TYP_CD 가 신규,변경일 경우) INSERT INTO WAM_SDITM ( SDITM_ID
) SELECT A.SDITM_ID
FROM WAQ_SDITM A LEFT JOIN WAM_DMN B ON A.DMN_ID = B.DMN_ID WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

--WAH이력 적재... INSERT INTO WAH_SDITM ( SDITM_ID , EXP_DTM , STR_DTM
) SELECT A.SDITM_ID , TO_DATE( '9999-12-31', 'YYYY-MM-DD') AS EXP_DTM , SYSDATETIME AS STR_DTM
FROM WAQ_SDITM A LEFT JOIN WAM_DMN B ON B.DMN_ID = A.DMN_ID WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1'
