mapper.select:
select 

from WAQ_STD_ERR_MSG
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_STD_ERR_MSG
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
    SELECT A.*
	  FROM WAQ_STD_ERR_MSG A
	 WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
	   AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

SELECT ((SELECT NVL(MAX(RQST_SNO), 0) FROM WAQ_STD_ERR_MSG WHERE RQST_NO = #{reqmst.rqstNo}) + ROWNUM) AS RQST_SNO , 'I' AS IBS_STATUS , A.STD_ERR_MSG_ID, A.STD_ERR_MSG_LNM, A.STD_ERR_MSG_PNM , #{reqmst.rqstDcd} AS RQST_DCD , A.OBJ_DESCN, A.OBJ_VERS , A.ERR_TLG_LAN_DSCD, A.ERR_TP_CD, A.MSG_DIT , A.STD_ERR_CD_YN, A.ACTN_CD, ERR_CD, A.ISD_ERR_CAS_CTS, A.ISD_ERR_ACTN_CTS, A.OSD_ERR_CAS_CTS , A.OSD_ERR_ACTN_CTS, A.ERR_CD_APL_DT, A.STSY_ERR_CD, A.TSK_DMN, A.MNG_USER, A.BIZ_DIT, A.MSG_TYP FROM WAM_STD_ERR_MSG A WHERE A.STD_ERR_MSG_ID IN
#{item.stdErrMsgId}
AND A.REG_TYP_CD IN ('C', 'U')

mapper.update:
 	UPDATE WAQ_STD_ERR_MSG SET STD_ERR_MSG_ID = #{stdErrMsgId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
