mapper.select:
--DQ Main 진단현황 차트 SQL SELECT MAX(BR_ANA_DGR) AS BR_ANA_DGR , MAX(BR_CNT) AS BR_CNT , MAX(BR_ANA_CNT) AS BR_ANA_CNT , MAX(BR_ERR_CNT) AS BR_ERR_CNT , NVL(MAX(PRF_ANA_DGR),0) AS PRF_ANA_DGR , MAX(PRF_CNT) AS PRF_CNT , MAX(PRF_ANA_CNT) AS PRF_ANA_CNT , MAX(PRF_ERR_CNT) AS PRF_ERR_CNT FROM (SELECT COUNT(M.BR_ID) AS BR_CNT , SUM(CASE WHEN R.BR_ID IS NOT NULL THEN 1 ELSE 0 END) AS BR_ANA_CNT , SUM(CASE WHEN R.ER_CNT > 0 THEN 1 ELSE 0 END) AS BR_ERR_CNT , MAX(R.ANA_DGR) AS BR_ANA_DGR , NULL AS PRF_CNT , NULL AS PRF_ANA_CNT , NULL AS PRF_ERR_CNT , NULL AS PRF_ANA_DGR FROM WAM_BR_MSTR M LEFT OUTER JOIN (SELECT R.* FROM WAM_BR_RESULT R WHERE ANA_DGR = (SELECT MAX(ANA_DGR) FROM WAM_BR_RESULT)) R ON M.BR_ID = R.BR_ID AND R.ANA_DGR IS NOT NULL WHERE M.REG_TYP_CD IN ('C','U')
UNION ALL SELECT NULL AS BR_CNT , NULL AS BR_ANA_CNT , NULL AS BR_ERR_CNT , NULL AS BR_ANA_DGR , COUNT(M.PRF_ID) AS PRF_CNT , SUM(CASE WHEN R.PRF_ID IS NOT NULL THEN 1 ELSE 0 END) AS PRF_ANA_CNT , SUM(CASE WHEN R.ESN_ER_CNT > 0 THEN 1 ELSE 0 END) AS PRF_ERR_CNT , MAX(R.ANA_DGR) AS PRF_ANA_DGR FROM WAM_PRF_MSTR M LEFT OUTER JOIN (SELECT R.* FROM WAM_PRF_RESULT R WHERE ANA_DGR = (SELECT MAX(ANA_DGR) FROM WAM_PRF_RESULT) ) R ON M.PRF_ID = R.PRF_ID AND R.ANA_DGR IS NOT NULL WHERE M.REG_TYP_CD IN ('C','U')
) A
