mapper.select:
SELECT /* WaaRqstAprpMapper.selectRqstApproveList */ A.* , A.USER_ID AS APRV_USER_ID , B.USER_NM AS APRV_USER_NM , C.USER_NM AS SBS_APRP_NM FROM WAA_RQST_APRP A INNER JOIN WAA_USER B ON A.USER_ID = B.USER_ID AND B.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND B.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAA_USER C ON A.SBS_APRP_ID = C.USER_ID AND C.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND C.REG_TYP_CD IN ('C', 'U')
AND A.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND A.REG_TYP_CD IN ('C', 'U')
AND A.USER_ID = #{userId,jdbcType=VARCHAR}
AND A.APRG_ID = #{aprgId,jdbcType=VARCHAR}
AND A.APR_FRML_CD = #{aprFrmlCd,jdbcType=VARCHAR}
AND A.SYS_DCD = #{sysDcd,jdbcType=VARCHAR}
AND A.BIZ_DCD = #{bizDcd,jdbcType=VARCHAR}
ORDER BY A.SYS_DCD, A.BIZ_DCD, A.APR_LVL, A.APR_FRML_CD

mapper.delete:

DELETE	/* WaaRqstAprpMapper.deleteRqstmapper */
FROM WAA_APR_PRC
WHERE RQST_NO=#{rqst_no}
