mapper.select:

	/* WaqMstrMapper.selectList */

  select 

  from WAQ_MSTR


      AND RQST_NM = #{rqstNm,jdbcType=VARCHAR}


      AND BIZ_DCD = #{bizDcd,jdbcType=VARCHAR}


      AND RQST_STEP_CD = #{rqstStepCd,jdbcType=VARCHAR}


      AND RQST_RESN = #{rqstResn,jdbcType=VARCHAR}


      AND WRIT_USER_ID = #{writUserId,jdbcType=VARCHAR}


      AND RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}


      AND APRV_USER_ID = #{aprvUserId,jdbcType=VARCHAR}

/* WaqMstrMapper.selectrequestMst */
select
, B.COMM_DTL_CD_NM AS BIZ_DCD_NM , C.COMM_DTL_CD_NM AS RQST_STEP_CD_NM from WAQ_MSTR M LEFT OUTER JOIN (SELECT B.COMM_DTL_CD, B.COMM_DTL_CD_NM FROM WAA_COMM_DCD A INNER JOIN WAA_COMM_DTL_CD B ON A.COMM_DCD_ID = B.COMM_DCD_ID AND A.COMM_DCD = 'BIZ_DCD' AND B.EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') AND B.REG_TYP_CD IN ('C','U') WHERE A.EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') AND A.REG_TYP_CD IN ('C','U') ) B ON M.BIZ_DCD = B.COMM_DTL_CD LEFT OUTER JOIN (SELECT B.COMM_DTL_CD, B.COMM_DTL_CD_NM FROM WAA_COMM_DCD A INNER JOIN WAA_COMM_DTL_CD B ON A.COMM_DCD_ID = B.COMM_DCD_ID AND A.COMM_DCD = 'RQST_STEP_CD' AND B.EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') AND B.REG_TYP_CD IN ('C','U') WHERE A.EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') AND A.REG_TYP_CD IN ('C','U') ) C ON M.RQST_STEP_CD = C.COMM_DTL_CD where RQST_NO = #{rqstNo,jdbcType=VARCHAR}

mapper.delete:

	/* WaqMstrMapper.deleteByPrimaryKey */

  delete from WAQ_MSTR
  where RQST_NO = #{rqstNo,jdbcType=VARCHAR}

mapper.update:

	/* WaqMstrMapper.updateWaqMstrAprvInfo */

  update WAQ_MSTR

  	RQST_STEP_CD = #{rqstStepCd,jdbcType=VARCHAR},
      APRV_DTM = NOW(),
      APRV_USER_ID = #{aprvUserId,jdbcType=VARCHAR},

  where RQST_NO = #{rqstNo,jdbcType=VARCHAR}


	/* WaqMstrMapper.updateWaqMstrRequestInfo */

  update WAQ_MSTR

      RQST_DTM = NOW(),
      RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR},

  where RQST_NO = #{rqstNo,jdbcType=VARCHAR}


	/* WaqMstrMapper.updateWaqMstrStepCd */

  UPDATE WAQ_MSTR

      RQST_STEP_CD = #{rqstStepCd},
      RQST_DTM = NOW(),
      RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR},

  WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}


  	/* WaqMstrMapper.updateWaqMstrqstNm */

	UPDATE WAQ_MSTR 
		SET RQST_NM = (
				SELECT 
					(CONCAT(B.BIZ_NM, ' ',A.${colnm} ,CASE WHEN B.CNT > 1 THEN  CONCAT(' 외 ', B.CNT-1, '건') ELSE '' END)) AS RQST_NM
				 FROM ${tblnm} A 
				JOIN (
					SELECT #{bizDtlNm,jdbcType=VARCHAR} AS BIZ_NM, 
						   COUNT(*) AS CNT, 
						   MIN(RQST_NO) AS RQST_NO, 
						   MIN(RQST_SNO) AS RQST_SNO
					FROM ${tblnm} 	
					WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
				) B
				  ON A.RQST_NO = B.RQST_NO
				 AND A.RQST_SNO = B.RQST_SNO
				 LIMIT 1
	)
	WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}


  	/* WaqMstrMapper.updateWaqMstrqstDtlNm */

	UPDATE WAQ_MSTR 
		SET RQST_NM = (
				SELECT 
					(CONCAT( B.BIZ_NM , ' ',A.${colnm} ,  CASE WHEN B.CNT > 1 THEN  CONCAT( ' 외 ', (B.CNT-1) , '건') ELSE '' END )) AS RQST_NM
				 FROM ${tblnm} A 
				JOIN (
					SELECT #{bizDtlNm,jdbcType=VARCHAR} AS BIZ_NM, 
						   COUNT(*) AS CNT, 
						   MIN(RQST_NO) AS RQST_NO, 
						   MIN(RQST_SNO) AS RQST_SNO,
						   MIN(RQST_DTL_SNO) AS RQST_DTL_SNO 
					FROM ${tblnm} 	
					WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
				) B
				  ON A.RQST_NO      = B.RQST_NO
				 AND A.RQST_SNO     = B.RQST_SNO
				 AND A.RQST_DTL_SNO = B.RQST_DTL_SNO
	)
	WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}


/* WaqMstrMapper.updateWaqMstrqstNm2 */

UPDATE WAQ_MSTR 
	SET RQST_NM = (
			SELECT 
				(CONCAT(B.BIZ_NM, ' ', A.${colnm},CASE WHEN B.CNT > 1 THEN  CONCAT(' 외 ', B.CNT-1, '건') ELSE '' END)) AS RQST_NM
			 FROM ${tblnm} A 
			JOIN (
				SELECT #{bizDtlNm,jdbcType=VARCHAR} AS BIZ_NM, 
					   COUNT(*) AS CNT, 
					   MIN(RQST_NO) AS RQST_NO, 
					   MIN(RQST_SNO) AS RQST_SNO
				FROM ${tblnm} 	
				WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
				  AND VRF_CD = '1'
			) B
			  ON A.RQST_NO = B.RQST_NO
			 AND A.RQST_SNO = B.RQST_SNO
			 LIMIT 1
)
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}

/* WaqMstrMapper.updateWaqMstrqstNmDic */
-- 요청서명 업데이트 (표준데이터 일경우...)
UPDATE WAQ_MSTR A INNER JOIN ( SELECT CASE WHEN MAX(RQST_NM1) IS NOT NULL OR MAX(RQST_NM2) IS NOT NULL OR MAX(RQST_NM3) IS NOT NULL THEN CONCAT('표준데이터 ' , IFNULL(CASE WHEN MAX(RQST_NM1) IS NOT NULL THEN CONCAT('[', MAX(RQST_NM1), ']') END,'') , IFNULL(CASE WHEN MAX(RQST_NM2) IS NOT NULL THEN CONCAT('[', MAX(RQST_NM2), ']') END,'') , IFNULL(CASE WHEN MAX(RQST_NM3) IS NOT NULL THEN CONCAT('[', MAX(RQST_NM3), ']') END,'') ,'[총 건수: ', SUM(CNT) , ']') END AS RQST_NM ,#{rqstNo,jdbcType=VARCHAR} AS RQST_NO FROM ( SELECT
(CONCAT(IFNULL(B.BIZ_NM,''), ': ',IFNULL(A.SDITM_LNM,''), CASE WHEN B.CNT > 1 THEN CONCAT(' 외 ', (B.CNT-1) , '건') ELSE '' END)) AS RQST_NM1 , null AS RQST_NM2 , null AS RQST_NM3 , CNT FROM WAQ_SDITM A JOIN ( SELECT '표준항목' AS BIZ_NM, COUNT(*) AS CNT, MIN(RQST_NO) AS RQST_NO, MIN(RQST_SNO) AS RQST_SNO FROM WAQ_SDITM WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR} ) B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO UNION ALL SELECT null AS RQST_NM1 ,(CONCAT(IFNULL(B.BIZ_NM,''), ': ',IFNULL(A.DMN_LNM,''), CASE WHEN B.CNT > 1 THEN CONCAT(' 외 ', (B.CNT-1) , '건') ELSE '' END)) AS RQST_NM2 , null AS RQST_NM3 , CNT FROM WAQ_DMN A JOIN ( SELECT '표준도메인' AS BIZ_NM, COUNT(*) AS CNT, MIN(RQST_NO) AS RQST_NO, MIN(RQST_SNO) AS RQST_SNO FROM WAQ_DMN WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR} ) B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO UNION ALL SELECT null AS RQST_NM1 , null AS RQST_NM2 ,(CONCAT(IFNULL(B.BIZ_NM,''), ': ',IFNULL(A.STWD_LNM,''), CASE WHEN B.CNT > 1 THEN CONCAT(' 외 ', (B.CNT-1) , '건') ELSE '' END)) AS RQST_NM3 , CNT FROM WAQ_STWD A JOIN ( SELECT '표준단어' AS BIZ_NM, COUNT(*) AS CNT, MIN(RQST_NO) AS RQST_NO, MIN(RQST_SNO) AS RQST_SNO FROM WAQ_STWD WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR} ) B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO ) AA )B ON A.RQST_NO = B.RQST_NO SET A.RQST_NM = B.RQST_NM WHERE A.RQST_NO =#{rqstNo,jdbcType=VARCHAR}

/* WaqMstrMapper.updateWaqMstrqstNmAdc */
-- 요청서명 업데이트 (APP데이터 일경우...) -- 요청서명 업데이트 (APP데이터 일경우...) UPDATE WAQ_MSTR A INNER JOIN ( SELECT CASE WHEN MAX(RQST_NM1) IS NOT NULL OR MAX(RQST_NM2) IS NOT NULL THEN CONCAT('APP데이터터 ' , IFNULL(CASE WHEN MAX(RQST_NM1) IS NOT NULL THEN CONCAT('[', MAX(RQST_NM1), ']') END,'') , IFNULL(CASE WHEN MAX(RQST_NM2) IS NOT NULL THEN CONCAT('[', MAX(RQST_NM2), ']') END,'') ,'[총 건수: ', SUM(CNT) , ']') END AS RQST_NM ,#{rqstNo,jdbcType=VARCHAR} AS RQST_NO FROM ( SELECT
(CONCAT(IFNULL(B.BIZ_NM,''), ': ',IFNULL(A.APP_SDITM_LNM,''), CASE WHEN B.CNT > 1 THEN CONCAT(' 외 ', (B.CNT-1) , '건') ELSE '' END)) AS RQST_NM1 , null AS RQST_NM2 , CNT FROM WAQ_APP_SDITM A JOIN ( SELECT 'APP용어' AS BIZ_NM, COUNT(*) AS CNT, MIN(RQST_NO) AS RQST_NO, MIN(RQST_SNO) AS RQST_SNO FROM WAQ_APP_SDITM WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR} ) B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO UNION ALL SELECT null AS RQST_NM1 ,(CONCAT(IFNULL(B.BIZ_NM,''), ': ',IFNULL(A.APP_STWD_LNM,''), CASE WHEN B.CNT > 1 THEN CONCAT(' 외 ', (B.CNT-1) , '건') ELSE '' END)) AS RQST_NM2 , CNT FROM WAQ_APP_STWD A JOIN ( SELECT 'APP단어' AS BIZ_NM, COUNT(*) AS CNT, MIN(RQST_NO) AS RQST_NO, MIN(RQST_SNO) AS RQST_SNO FROM WAQ_APP_STWD WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR} ) B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO )AA )B ON A.RQST_NO = B.RQST_NO SET A.RQST_NM = B.RQST_NM WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}

/* WaqMstrMapper.updateWaqMstrqstNmDtt */
-- 요청서명 업데이트 (DDL 이관요청 일경우...) UPDATE WAQ_MSTR A INNER JOIN ( SELECT CASE WHEN MAX(RQST_NM1) IS NOT NULL OR MAX(RQST_NM2) IS NOT NULL THEN CONCAT('DDL이관 ' , IFNULL(CASE WHEN MAX(RQST_NM1) IS NOT NULL THEN CONCAT('[', MAX(RQST_NM1), ']') END,'') , IFNULL(CASE WHEN MAX(RQST_NM2) IS NOT NULL THEN CONCAT('[', MAX(RQST_NM2), ']') END,'') ,'[총 건수: ', SUM(CNT) , ']') END AS RQST_NM ,#{rqstNo,jdbcType=VARCHAR} AS RQST_NO FROM ( SELECT
(CONCAT(IFNULL(B.BIZ_NM,''), ': ',IFNULL(A.DDL_TBL_PNM,''), CASE WHEN B.CNT > 1 THEN CONCAT(' 외 ', (B.CNT-1) , '건') ELSE '' END)) AS RQST_NM1 , null AS RQST_NM2 , null AS RQST_NM3 , CNT FROM WAQ_DDL_TSF_TBL A JOIN ( SELECT 'DDL테이블' AS BIZ_NM, COUNT(*) AS CNT, MIN(RQST_NO) AS RQST_NO, MIN(RQST_SNO) AS RQST_SNO FROM WAQ_DDL_TSF_TBL WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR} ) B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO UNION ALL SELECT null AS RQST_NM1 ,(CONCAT(IFNULL(B.BIZ_NM,''), ': ',IFNULL(A.DDL_IDX_PNM,''), CASE WHEN B.CNT > 1 THEN CONCAT(' 외 ', (B.CNT-1) , '건') ELSE '' END)) AS RQST_NM2 , null AS RQST_NM3 , CNT FROM WAQ_DDL_TSF_IDX A JOIN ( SELECT 'DDL인덱스' AS BIZ_NM, COUNT(*) AS CNT, MIN(RQST_NO) AS RQST_NO, MIN(RQST_SNO) AS RQST_SNO FROM WAQ_DDL_TSF_IDX WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR} ) B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO )AA )B ON A.RQST_NO = B.RQST_NO SET A.RQST_NM = B.RQST_NM WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}


   /* WaqMstrMapper.updateWaqMstrqstNmDDP */

 	UPDATE WAQ_MSTR 
		SET RQST_NM = (
				SELECT CONCAT(B.BIZ_NM,'',CONCAT(A.DDL_TBL_PNM ,(case when C.DDL_TBL_LNM IS NULL then '' else CONCAT('(', C.DDL_TBL_LNM, ')'), CASE WHEN B.CNT > 1 THEN  CONCAT(' 외 ', (B.CNT-1) , '건') ELSE '' end))) AS RQST_NM
				  FROM WAQ_DDL_PART A
				       INNER JOIN (SELECT #{bizDtlNm,jdbcType=VARCHAR} AS BIZ_NM, 
										   COUNT(*) AS CNT, 
										   MIN(RQST_NO) AS RQST_NO, 
										   MIN(RQST_SNO) AS RQST_SNO
									FROM WAQ_DDL_PART
									WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
								) B
						  ON A.RQST_NO = B.RQST_NO
						 AND A.RQST_SNO = B.RQST_SNO 
		              LEFT OUTER JOIN WAM_DDL_TBL C
		                ON A.DDL_TBL_ID = C.DDL_TBL_ID
		         WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}  
				)
	WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}


   /* WaqMstrMapper.updateWaqMstrqstNmDDP */

 	UPDATE WAQ_MSTR 
		SET RQST_NM = (
				SELECT B.BIZ_NM || ' ' || (A.DDL_TBL_PNM || DECODE(C.DDL_TBL_LNM, NULL, '', '('||C.DDL_TBL_LNM||')' ) || CASE WHEN B.CNT > 1 THEN  ' 외 '|| (B.CNT-1) || '건' ELSE '' END ) AS RQST_NM
				  FROM WAQ_DDL_PART A
				       INNER JOIN (SELECT #{bizDtlNm,jdbcType=VARCHAR} AS BIZ_NM, 
										   COUNT(*) AS CNT, 
										   MIN(RQST_NO) AS RQST_NO, 
										   MIN(RQST_SNO) AS RQST_SNO
									FROM WAQ_DDL_PART
									WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
								) B
						  ON A.RQST_NO = B.RQST_NO
						 AND A.RQST_SNO = B.RQST_SNO 
		              LEFT OUTER JOIN WAM_DDL_TBL C
		                ON A.DDL_TBL_ID = C.DDL_TBL_ID
		         WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}  
				)
	WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}

/* WaqMstrMapper.updateWaqMstrqstNmSBC */
-- 요청서명 업데이트 (표준업무동시등록 일경우...)
UPDATE WAQ_MSTR A INNER JOIN ( SELECT CASE WHEN MAX(RQST_NM1) IS NOT NULL OR MAX(RQST_NM2) IS NOT NULL THEN CONCAT('표준/업무오류코드 ' , IFNULL(CASE WHEN MAX(RQST_NM1) IS NOT NULL THEN CONCAT('[', MAX(RQST_NM1), ']') END,'') , IFNULL(CASE WHEN MAX(RQST_NM2) IS NOT NULL THEN CONCAT('[', MAX(RQST_NM2), ']') END,'') ,'[총 건수: ', SUM(CNT) , ']') END AS RQST_NM , #{rqstNo,jdbcType=VARCHAR} AS RQST_NO FROM ( SELECT
(CONCAT(IFNULL(B.BIZ_NM,''), ': ',IFNULL(A.ISD_ERR_CAS_CTS,''), CASE WHEN B.CNT > 1 THEN CONCAT(' 외 ', (B.CNT-1) , '건') ELSE '' END)) AS RQST_NM1 , null AS RQST_NM2 , null AS RQST_NM3 , CNT FROM WAQ_STD_ERR_CD A JOIN ( SELECT '표준오류코드' AS BIZ_NM, COUNT(*) AS CNT, MIN(RQST_NO) AS RQST_NO, MIN(RQST_SNO) AS RQST_SNO FROM WAQ_STD_ERR_CD WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR} ) B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO UNION ALL SELECT null AS RQST_NM1 ,(CONCAT(IFNULL(B.BIZ_NM,''), ': ',IFNULL(A.BIZ_ERR_CD,''), CASE WHEN B.CNT > 1 THEN CONCAT(' 외 ', (B.CNT-1) , '건') ELSE '' END)) AS RQST_NM2 , null AS RQST_NM3 , CNT FROM WAQ_BIZ_ERR_CD A JOIN ( SELECT '업무오류코드' AS BIZ_NM, COUNT(*) AS CNT, MIN(RQST_NO) AS RQST_NO, MIN(RQST_SNO) AS RQST_SNO FROM WAQ_BIZ_ERR_CD WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR} ) B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO )AA )B ON A.RQST_NO = B.RQST_NO SET A.RQST_NM = B.RQST_NM WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}

mapper.select:
/* WaqMstrMapper.selectRqstMyListCount */
SELECT COUNT(*) AS RQST_MY_COUNT
FROM ( SELECT A.RQST_NO, A.BIZ_DCD, A.RQST_NM, A.RQST_DTM, GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM, A.RQST_STEP_CD, A.RQST_USER_ID, B.APR_LVL, B.USER_ID, B.APRV_DTM, B.APR_FRML_CD, CASE WHEN A.RQST_STEP_CD = 'A' THEN '결재 완료'
WHEN B.APR_FRML_CD = 'G' THEN CONCAT(IFNULL((SELECT APRG_NM FROM WAA_APRG WHERE APRG_ID = B.USER_ID AND REG_TYP_CD IN ('C', 'U') AND EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d')), '') , '그룹') WHEN B.APR_FRML_CD IN ('D','T','J','A') THEN CONCAT(IFNULL((SELECT GET_USER_NM(B.USER_ID) FROM DUAL), '') , '님') END AS APRV_STEP_LVL_TEMP, CASE -- 결재내역중 가장 마지막 결재건, 전결된 내역, 미결재내역중 현재 결재대상건 확인 WHEN (RQST_STEP_CD = 'A' AND B.APR_LVL = (SELECT MAX(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NOT NULL) OR RQST_STEP_CD = 'Q' AND B.APR_LVL = (SELECT MIN(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NULL)) THEN '1'
ELSE '0' END AS CHECK_APR_LVL
FROM WAQ_MSTR A INNER JOIN WAA_APR_PRC B ON A.RQST_NO =B.RQST_NO ) A
CHECK_APR_LVL = '1' AND RQST_STEP_CD = 'Q'
AND RQST_NO = #{rqstNo,jdbcType=VARCHAR}
AND RQST_NM LIKE CONCAT('%',#{rqstNm,jdbcType=VARCHAR},'%')
AND RQST_USER_NM = #{rqstUserNm,jdbcType=VARCHAR}
AND RQST_STEP_CD = #{rqstStepCd,jdbcType=VARCHAR}
AND RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
ORDER BY RQST_STEP_CD, RQST_NO, APR_LVL

/* WaqMstrMapper.selectRqstMyListForMain */
SELECT A.* , CONCAT(IFNULL(APR_LVL, '') , ' / ' , IFNULL((SELECT MAX(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO), '') , '단계 (' , IFNULL(APRV_STEP_LVL_TEMP, '') , ')') AS APRV_STEP_LVL ,( SELECT B.COMM_DTL_CD_NM FROM WAA_COMM_DCD A inner JOIN WAA_COMM_DTL_CD B ON A.COMM_DCD_ID = B.COMM_DCD_ID AND A.REG_TYP_CD IN ('C', 'U') AND A.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND B.REG_TYP_CD IN ('C', 'U') AND B.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') WHERE A.COMM_DCD = 'BIZ_DCD' AND B.COMM_DTL_CD = A.BIZ_DCD) AS BIZ_DCD_NM FROM ( SELECT * FROM ( SELECT A.RQST_NO, A.BIZ_DCD, A.RQST_NM, A.RQST_DTM, GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM, A.RQST_STEP_CD, A.RQST_USER_ID, B.APR_LVL, B.USER_ID, B.APRV_DTM, B.APR_FRML_CD, GET_CMCD_DTL_NM('BIZ_DCD',A.BIZ_DCD) AS BIZ_DCD_NM_NOUSE, CASE WHEN A.RQST_STEP_CD = 'A' AND B.RVW_STS_CD=1 THEN '결재 완료' WHEN A.RQST_STEP_CD = 'A' AND B.RVW_STS_CD=2 THEN '반려' WHEN B.APR_FRML_CD = 'G' THEN ( SELECT APRG_NM FROM WAA_APRG WHERE APRG_ID = B.USER_ID AND EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND CONCAT(REG_TYP_CD IN ('C', 'U'), '그룹') ) WHEN B.APR_FRML_CD IN ('D','T','J','A') THEN CONCAT( (SELECT GET_USER_NM(B.USER_ID) FROM DUAL) , '님') END AS APRV_STEP_LVL_TEMP, CASE -- 결재내역중 가장 마지막 결재건, 전결된 내역, 미결재내역중 현재 결재대상건 확인 WHEN (RQST_STEP_CD = 'A' AND B.APR_LVL = (SELECT MAX(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NOT NULL) OR RQST_STEP_CD = 'Q' AND B.APR_LVL = (SELECT MIN(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NULL)) THEN '1'
ELSE '0' END AS CHECK_APR_LVL
FROM WAQ_MSTR A INNER JOIN WAA_APR_PRC B ON A.RQST_NO =B.RQST_NO ) TBL
CHECK_APR_LVL = '1' AND RQST_STEP_CD != 'S'
AND RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
) A ORDER BY RQST_DTM DESC, RQST_NO, APR_LVL LIMIT 0,5

/* WaqMstrMapper.selectRqstResultList */
SELECT A.RQST_NO, A.BIZ_DCD, A.RQST_NM, A.RQST_DTM, A.RQST_STEP_CD, A.RQST_USER_NM, MAX(A.APR_LVL) AS APR_LVL, A.USER_ID, MAX(A.APRV_DTM) AS APRV_DTM, A.APR_FRML_CD, CONCAT(IFNULL(MAX(A.APR_LVL), '') , ' / ' , IFNULL((SELECT MAX(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO), '') , '단계') AS APRV_STEP_LVL , A.CHECK_APR_LVL , CASE WHEN A.RQST_STEP_CD = 'Q' THEN '#0000FF' END AS FONTCOLOR FROM ( SELECT A.RQST_NO , A.BIZ_DCD , A.RQST_NM , A.RQST_DTM , A.RQST_STEP_CD , GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM , B.APR_LVL , B.USER_ID , B.APRV_DTM , B.APR_FRML_CD -- 그룹결재인지 개인결재인지
, CASE WHEN -- 개인 결재 결재자ID 본인 B.USER_ID = #{rqstUserId} AND B.APR_FRML_CD IN ('D','T','J','A') AND B.APR_LVL = (SELECT MAX(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NOT NULL) THEN '1' ELSE CASE WHEN B.APR_FRML_CD = 'G' AND B.USER_ID IN (SELECT APRG_ID FROM WAA_APRG_USER WHERE APRG_ID = B.APRG_ID AND EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND REG_TYP_CD IN ('C', 'U') AND USER_ID = #{rqstUserId}) AND B.APR_LVL = (SELECT MAX(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NOT NULL) THEN '1' ELSE CASE WHEN B.APR_FRML_CD IN ('D','T','J','A') AND B.USER_ID != #{rqstUserId} AND B.USER_ID IN (SELECT USER_ID FROM WAA_RQST_APRP WHERE 1=1 AND ABD_DAPR_DCD = 'D' AND SBS_APRP_ID = #{rqstUserId} AND DAPR_STR_DT <= DATE_FORMAT(NOW(), '%Y-%m-%d') AND DAPR_END_DT >= DATE_FORMAT(NOW(), '%Y-%m-%d') -- 대신결재(대결)자에 이름이 있는 경우. ) AND B.APR_LVL = (SELECT MAX(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NOT NULL) THEN '1' ELSE '0' END END END AS CHECK_APR_LVL
FROM WAQ_MSTR A INNER JOIN WAA_APR_PRC B ON A.RQST_NO = B.RQST_NO ) A
AND A.RQST_STEP_CD = 'A' AND A.USER_ID = #{rqstUserId}
AND BIZ_DCD = #{bizDcd,jdbcType=VARCHAR}
AND RQST_NM LIKE CONCAT('%' , #{rqstNm,jdbcType=VARCHAR} , '%')
AND DATE_FORMAT(RQST_DTM, '%Y%m%d') >= DATE_FORMAT(STR_TO_DATE(#{searchBgnDe,jdbcType=VARCHAR}, '%Y-%m-%d'), '%Y%m%d')
AND DATE_FORMAT(RQST_DTM, '%Y%m%d') <= DATE_FORMAT(STR_TO_DATE(#{searchEndDe,jdbcType=VARCHAR}, '%Y-%m-%d'), '%Y%m%d')
GROUP BY A.RQST_NO, A.BIZ_DCD, A.RQST_NM, A.RQST_DTM, A.RQST_STEP_CD, A.RQST_USER_NM, A.USER_ID, A.APR_FRML_CD, A.CHECK_APR_LVL ORDER BY RQST_DTM DESC, RQST_NO, APR_LVL

/* WaqMstrMapper.selectRqstToDoListCount */
SELECT COUNT(*) AS RQST_TO_DO_COUNT FROM ( SELECT A.RQST_NO ,A.BIZ_DCD ,A.RQST_NM ,A.RQST_DTM ,A.RQST_STEP_CD ,GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM ,B.APR_LVL ,B.USER_ID ,B.APRV_DTM ,B.APR_FRML_CD -- 그룹결재인지 개인결재인지 ,CASE WHEN RQST_STEP_CD = 'Q'
AND B.USER_ID = #{rqstUserId} -- 실 로그인아이디
AND B.APR_FRML_CD = 'D' AND B.APR_LVL = (SELECT MIN(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NULL) -- 결재대상자가 나인 경우
OR RQST_STEP_CD = 'Q' AND B.APR_FRML_CD = 'G'
AND B.USER_ID IN (SELECT APRG_ID FROM WAA_APRG_USER WHERE APRG_ID = B.APRG_ID AND EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND REG_TYP_CD IN ('C', 'U')
AND USER_ID = #{rqstUserId} -- 결재그룹이 내가 속한 그룹인 경우, 실 로그인아이디
) AND B.APR_LVL = (SELECT MIN(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NULL) OR RQST_STEP_CD = 'Q' AND B.APR_FRML_CD = 'D'
AND B.USER_ID != #{rqstUserId}
AND B.USER_ID IN (SELECT USER_ID FROM WAA_RQST_APRP WHERE 1=1 AND ABD_DAPR_DCD = 'D'
AND SBS_APRP_ID = #{rqstUserId}
AND DAPR_STR_DT <= DATE_FORMAT(NOW(), '%Y-%m-%d') AND DAPR_END_DT >= DATE_FORMAT(NOW(), '%Y-%m-%d') -- 대신결재(대결)자에 이름이 있는 경우.
) AND B.APR_LVL = (SELECT MIN(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NULL) THEN '1'
ELSE '0' END AS CHECK_APR_LVL , CONCAT(IFNULL(APR_LVL, '') , ' / ' , IFNULL((SELECT MAX(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO), '') , '단계') AS APRV_STEP_LVL FROM WAQ_MSTR A INNER JOIN WAA_APR_PRC B ON A.RQST_NO = B.RQST_NO ) TBL
CHECK_APR_LVL = '1'
AND BIZ_DCD = #{bizDcd,jdbcType=VARCHAR}
AND RQST_NM LIKE CONCAT('%', #{rqstNm,jdbcType=VARCHAR},'%')
AND DATE_FORMAT(RQST_DTM, '%Y%m%d' >= DATE_FORMAT(STR_TO_DATE(#{searchBgnDe,jdbcType=VARCHAR}, '%Y-%m-%d'), '%Y%m%d')
AND DATE_FORMAT(RQST_DTM, '%Y%m%d' <= DATE_FORMAT(STR_TO_DATE(#{searchEndDe,jdbcType=VARCHAR}, '%Y-%m-%d'), '%Y%m%d')
ORDER BY RQST_NO, APR_LVL

/* WaqMstrMapper.selectRqstCount */
SELECT IFNULL(SUM(CASE WHEN RQST_STEP_CD = 'Q' THEN 1 ELSE 0 END), 0) AS RQST_MY_COUNT ,IFNULL(SUM(CASE WHEN RQST_STEP_CD = 'S' THEN 1 ELSE 0 END), 0) AS RQST_TMP_COUNT FROM WAQ_MSTR WHERE RQST_STEP_CD IN ('S', 'Q') AND RQST_NM IS NOT NULL
AND RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}

/* WaqMstrMapper.selectRejMyList */
SELECT A.* , CASE WHEN APRV_STEP_LVL_TEMP IS NULL THEN '' WHEN APRV_STEP_LVL_TEMP IS NOT NULL AND APR_LVL IS NULL THEN CONCAT(IFNULL(APRV_STEP_LVL_TEMP, '') , '(결재단계 없음)') ELSE CONCAT(IFNULL(APR_LVL, '') , ' / ' , IFNULL((SELECT MAX(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO), '') , '단계 (' , IFNULL(APRV_STEP_LVL_TEMP, '') , ')') END AS APRV_STEP_LVL , CASE WHEN RQST_STEP_CD = 'A' THEN '#0000FF' ELSE NULL END AS FontColor
FROM ( SELECT A.RQST_NO, A.BIZ_DCD, A.RQST_NM, A.RQST_DTM, GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM, A.RQST_STEP_CD, A.RQST_USER_ID, B.APR_LVL, B.USER_ID, B.APRV_DTM, B.APR_FRML_CD, A.WRIT_USER_ID, B.RVW_STS_CD, CASE WHEN A.RQST_STEP_CD = 'A' AND B.RVW_STS_CD = '2' THEN '반려' WHEN A.RQST_STEP_CD = 'A' AND B.RVW_STS_CD = '1' THEN '승인' WHEN A.RQST_STEP_CD = 'A' AND B.RVW_STS_CD = '3' THEN '부분승인' WHEN A.RQST_STEP_CD = 'A' AND B.RVW_STS_CD IS NULL THEN '자동승인' END AS APRV_STATUS,
CASE WHEN A.RQST_STEP_CD = 'A' THEN '결재 완료'
WHEN B.APR_FRML_CD = 'G' THEN CONCAT(( SELECT APRG_NM FROM WAA_APRG WHERE APRG_ID = B.USER_ID AND REG_TYP_CD IN ('C', 'U') AND EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') ) ,'그룹') WHEN B.APR_FRML_CD IN ('D','T','J','A') THEN CONCAT((SELECT GET_USER_NM(B.USER_ID) FROM DUAL), '님') END AS APRV_STEP_LVL_TEMP, CASE -- 결재내역중 가장 마지막 결재건, 전결된 내역, 미결재내역중 현재 결재대상건 확인 WHEN (RQST_STEP_CD = 'A' AND B.APR_LVL = (SELECT MAX(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NOT NULL) OR RQST_STEP_CD = 'A' AND B.APR_LVL IS NULL OR RQST_STEP_CD = 'Q' AND B.APR_LVL = (SELECT MIN(APR_LVL) FROM WAA_APR_PRC WHERE RQST_NO = A.RQST_NO AND APRV_DTM IS NULL)) OR RQST_STEP_CD = 'S' THEN '1'
ELSE '0' END AS CHECK_APR_LVL
FROM WAQ_MSTR A LEFT JOIN WAA_APR_PRC B ON A.RQST_NO =B.RQST_NO ) A
CHECK_APR_LVL = '1' AND RQST_NM IS NOT NULL
AND RQST_NO = #{rqstNo,jdbcType=VARCHAR}
AND BIZ_DCD = #{bizDcd,jdbcType=VARCHAR}
AND RQST_NM LIKE CONCAT('%' , #{rqstNm,jdbcType=VARCHAR} , '%')
AND RQST_USER_NM = #{rqstUserNm,jdbcType=VARCHAR}
AND RQST_STEP_CD = #{rqstStepCd,jdbcType=VARCHAR}
AND WRIT_USER_ID = #{writUserId,jdbcType=VARCHAR}
AND RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
AND DATE_FORMAT(RQST_DTM, '%Y%m%d') >= DATE_FORMAT(STR_TO_DATE(#{searchBgnDe,jdbcType=VARCHAR}, '%Y-%m-%d'), '%Y%m%d')
AND DATE_FORMAT(RQST_DTM, '%Y%m%d') <= DATE_FORMAT(STR_TO_DATE(#{searchEndDe,jdbcType=VARCHAR}, '%Y-%m-%d'), '%Y%m%d')
AND RVW_STS_CD IN ('2', '3')
ORDER BY RQST_DTM DESC, RQST_STEP_CD, RQST_NO, APR_LVL

mapper.update:

	/* WaqMstrMapper.updateWaqMstrDbaAfterAprgY */

  update WAQ_MSTR

  	DBA_AFTER_APRG_YN = 'Y'

  where RQST_NO = #{rqstNo,jdbcType=VARCHAR}


