mapper.delete:

 delete	/* WaaBscLvlMapper.deleteByPrimaryKey */
	from WAA_BSC_LVL
 where BSC_ID = #{bscId,jdbcType=VARCHAR}
   and EXP_DTM = #{expDtm,jdbcType=TIMESTAMP}

mapper.select:
SELECT /* WaaBscLvlMapper.selectList */
,GET_USER_NM(WRIT_USER_ID) AS WRIT_USER_NM FROM WAA_BSC_LVL
AND EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND REG_TYP_CD IN ('C', 'U')
AND BSC_ID = #{bscId,jdbcType=VARCHAR}
AND BSC_LVL = #{bscLvl,jdbcType=DECIMAL}

SELECT COUNT(*) FROM WAA_BSC_LVL A
AND A.REG_TYP_CD IN ('C', 'U') AND A.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d')
AND BSC_ID = #{bscId,jdbcType=VARCHAR}
