mapper.select:
SELECT A.DQI_ID , A.DQI_LNM , A.DQI_PNM , A.DQI_LVL , A.UPP_DQI_ID , A.GOAL , A.WEIGHT , A.RQST_NO , A.RQST_SNO , A.OBJ_DESCN , A.OBJ_VERS , A.REG_TYP_CD , A.FRS_RQST_DTM , A.FRS_RQST_USER_ID , GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM , A.RQST_DTM , A.RQST_USER_ID , GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM , A.APRV_DTM , A.APRV_USER_ID , GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM , A.FULL_PATH , B.DQI_LNM AS UPP_DQI_LNM , C.BIZRULE_CNT , A.VRFC_TYP FROM (
) A LEFT OUTER JOIN WAM_DQI B ON A.UPP_DQI_ID = B.DQI_ID AND B.REG_TYP_CD IN ('C','U') LEFT OUTER JOIN (SELECT COUNT(1) AS BIZRULE_CNT , B.DQI_ID AS JOIN_DQI_ID FROM WAM_BR_MSTR A INNER JOIN WAM_BR_DQI_MAP B ON A.BR_ID = B.BR_ID AND A.REG_TYP_CD IN ('C', 'U') AND B.REG_TYP_CD IN ('C', 'U') GROUP BY DQI_ID) C ON A.DQI_ID = C.JOIN_DQI_ID
and A.DQI_ID = #{dqiId,jdbcType=VARCHAR}
and A.DQI_LNM LIKE CONCAT('%',#{dqiLnm,jdbcType=VARCHAR},'%')
and A.UPP_DQI_LNM LIKE CONCAT('%', #{uppDqiLnm,jdbcType=VARCHAR}, '%')
ORDER BY CASE WHEN A.DQI_ID IN ('OBJ_00000095194', 'OBJ_00000095195') THEN 1 ELSE 0 END, A.FULL_PATH, A.DQI_LVL

mapper.delete:
delete WAM_DQI
where DQI_ID in (select DQI_ID from WAH_DQI
	                            where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
	                               and EXP_DTM = '99991231'
	                 )
