FROM WDQQ_STWD A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

-- WAH이력 적재... INSERT INTO WDQH_STWD ( STWD_ID , EXP_DTM , STR_DTM
) SELECT A.STWD_ID AS STWD_ID , '99991231' AS EXP_DTM , SYSDATE AS STR_DTM
FROM WDQQ_STWD A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1'


	-- 검증쿼리
	  AND A.REG_TYP_CD = 'C'   
