mapper.select:
select /* WaqColMapMapper.selectByPrimaryKey */

from WAQ_COL_MAP
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete /* WaqColMapMapper.deleteByPrimaryKey */
from WAQ_COL_MAP
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
select /* WaqColMapMapper.selectColMapDetail */

from WAQ_COL_MAP
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
DELETE 	/* WaqColMapMapper.deleteByrqstSno */
		FROM WAQ_COL_MAP
 WHERE RQST_NO  = #{rqstNo}
   AND RQST_SNO = #{rqstSno}

mapper.insert:

    /* WaqColMapMapper.checkAppCrgp */



	  AND EXISTS ( SELECT 1
		           FROM WAM_COL_MAP B
		           WHERE IFNULL(A.APP_CRGP_NM, 'X') != IFNULL(B.APP_CRGP_NM, 'O')
	                 AND IFNULL(A.MAP_DF_ID, 'X') = IFNULL(B.MAP_DF_ID, 'O')
		         )


  /* WaqColMapMapper.checkNotExistTgtTbl */



      	AND A.REG_TYP_CD IN ('C', 'U')
		AND NOT EXISTS (SELECT 1
		                  FROM WAM_PDM_TBL D
		                  WHERE A.TGT_TBL_PNM = D.PDM_TBL_PNM
		               )


    /* WaqColMapMapper.checkNotExistTgtCol */



      	AND A.REG_TYP_CD IN ('C', 'U')
		AND NOT EXISTS (SELECT 1
		                  FROM WAM_PDM_COL D
		                  WHERE A.TGT_COL_PNM = D.PDM_COL_PNM
		               )


   /* WaqColMapMapper.checkNotExistMapDfId */



      	AND A.REG_TYP_CD IN ('C', 'U')
		AND NOT EXISTS (SELECT 1
		                  FROM WAM_TBL_MAP D
		                  WHERE A.MAP_DF_ID = D.MAP_DF_ID
		               )

/* WaqColMapMapper.checkNotExistAsisMapTbl */
AND A.REG_TYP_CD IN ('C', 'U') AND NOT EXISTS (SELECT 1 FROM WAM_TBL_MAP D WHERE A.MAP_DF_ID = D.MAP_DF_ID
AND IFNULL(A.SRC_DB_PNM, 'X') = IFNULL(D.SRC_DB_PNM, 'X') AND IFNULL(A.SRC_TBL_PNM, 'X') = IFNULL(D.SRC_TBL_PNM, 'X') UNION ALL SELECT 1 FROM WAM_TBL_MAP D WHERE A.SRC_TBL_PNM = D.TGT_TBL_PNM ) AND IFNULL(A.COL_MAP_TYPE,'1') != '2'

mapper.update:

   UPDATE /* WaqColMapMapper.updateidByKey */
   		WAQ_COL_MAP SET COL_MAP_ID = #{tblMapId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

UPDATE /* WaqColMapMapper.updateWaqCUD */ WAQ_COL_MAP A LEFT JOIN WAM_COL_MAP B ON B.MAP_DF_ID = A.MAP_DF_ID AND B.TGT_COL_PNM = A.TGT_COL_PNM AND B.COL_MAP_ID = A.COL_MAP_ID
SET A.COL_MAP_ID = B.COL_MAP_ID , A.OBJ_VERS = IFNULL(B.OBJ_VERS, 0) + 1 , A.FRS_RQST_DTM = B.FRS_RQST_DTM , A.FRS_RQST_USER_ID = B.FRS_RQST_USER_ID
WHERE A.RQST_NO = #{rqstNo}
AND A.RVW_STS_CD = '1' AND EXISTS ( SELECT 1 FROM WAM_COL_MAP B WHERE B.MAP_DF_ID = A.MAP_DF_ID AND B.TGT_COL_PNM = A.TGT_COL_PNM AND B.COL_MAP_ID = A.COL_MAP_ID
)
