mapper.select:
select /* WaqPdmRelColMapper.selectByPrimaryKey */

from WAQ_PDM_REL_COL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}
  and PDM_REL_PNM = #{pdmRelPnm,jdbcType=VARCHAR}
  and PA_TBL_PNM = #{paTblPnm,jdbcType=VARCHAR}
  and PA_COL_PNM = #{paColPnm,jdbcType=VARCHAR}
  and CH_TBL_PNM = #{chTblPnm,jdbcType=VARCHAR}
  and CH_COL_PNM = #{chColPnm,jdbcType=VARCHAR}

mapper.delete:
DELETE /* WaqPdmRelColMapper.deleteByPrimaryKey */
FROM WAQ_PDM_REL_COL
 WHERE RQST_NO          = #{rqstNo,jdbcType=VARCHAR}
   AND RQST_SNO         = #{rqstSno,jdbcType=DECIMAL}
   AND RQST_DTL_SNO     = #{rqstDtlSno,jdbcType=DECIMAL}
   AND RQST_DTL_DTL_SNO = #{rqstDtlDtlSno,jdbcType=DECIMAL}

mapper.select:
SELECT /* WaqPdmRelColMapper.selectPdmRelColRqstList */
, CASE WHEN A.VRF_CD = '2' THEN '#FF0000' ELSE CASE WHEN A.VRF_CD = '5' THEN '#0000FF' END END AS FontColor , U.USER_NM AS RQST_USER_NM FROM WAQ_PDM_REL_COL A LEFT JOIN WAA_USER U ON A.RQST_USER_ID = U.USER_ID AND U.REG_TYP_CD IN ('C', 'U') AND U.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') WHERE A.RQST_NO = #{rqstNo}
AND A.RQST_SNO = #{rqstSno}
AND A.RQST_DTL_SNO = #{rqstDtlSno}
ORDER BY A.RQST_SNO , A.RQST_DTL_SNO , A.RQST_DTL_DTL_SNO , A.PA_FULL_PATH , A.PA_TBL_PNM

select /* WaqPdmRelColMapper.selectPdmRelDetail */

from WAQ_PDM_REL_COL
where RQST_NO      = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO     = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

SELECT 	/* WaqPdmRelColMapper.selectWaqRelMstRqstSno */
			MAX(RQST_SNO) AS RQST_SNO    
  FROM WAQ_PDM_REL_COL
 WHERE RQST_NO      = #{rqstNo, jdbcType=VARCHAR}
   AND PA_FULL_PATH  = #{paFullPath, jdbcType=VARCHAR}
   AND PA_TBL_PNM  = #{paTblPnm, jdbcType=VARCHAR}

mapper.insert:
/* WaqPdmRelColMapper.checkNotDtl */
--검증쿼리 : 관계명 내용 불일치 (PR007) AND A.RQST_DCD = 'CU' AND EXISTS (SELECT 1 FROM WAQ_PDM_REL_COL D WHERE D.RQST_NO = A.RQST_NO AND D.RQST_SNO = A.RQST_SNO AND D.RQST_DTL_SNO = A.RQST_DTL_SNO AND ( D.PDM_REL_LNM != A.PDM_REL_LNM OR D.CRD_TYP_CD != A.CRD_TYP_CD OR D.REL_TYP_CD != A.REL_TYP_CD OR D.PA_OPT_TYP_CD != A.PA_OPT_TYP_CD )
)

/* WaqPdmRelColMapper.checkNotExistRel */
--검증쿼리 : 삭제대상 미존재 관계 (PC009) AND A.RQST_DCD = 'DD' AND NOT EXISTS (SELECT 1 FROM WAM_PDM_REL D INNER JOIN WAM_PDM_TBL E ON E.PDM_TBL_ID = D.CH_TBL_ID AND E.SUBJ_ID = D.CH_SUBJ_ID AND E.REG_TYP_CD IN ('C','U') INNER JOIN WAQ_PDM_TBL F ON F.FULL_PATH = E.FULL_PATH AND F.PDM_TBL_PNM = E.PDM_TBL_PNM
WHERE F.RQST_NO = A.RQST_NO AND F.RQST_SNO = A.RQST_SNO AND D.PDM_REL_PNM = A.PDM_REL_PNM )

/* WaqPdmRelColMapper.checkRequestRel */
--검증쿼리 : 요청중인 관계(요청중인 테이블일 경우 전체 적용...) (PR010) AND EXISTS ( SELECT 1 FROM WAQ_MSTR E INNER JOIN WAQ_PDM_TBL D ON E.RQST_NO = D.RQST_NO AND E.RQST_NO != #{rqstNo} AND E.RQST_STEP_CD = 'Q' AND IFNULL(D.RVW_STS_CD, 0) != '2' INNER JOIN WAQ_PDM_TBL F ON F.PDM_TBL_PNM = D.PDM_TBL_PNM
AND F.FULL_PATH = D.FULL_PATH WHERE F.RQST_NO = A.RQST_NO AND F.RQST_SNO = A.RQST_SNO )

/* WaqPdmRelColMapper.checkNotConformEnty */
--검증쿼리 : 부모, 자식엔터티중 하나는 테이블명과 일치해야함 (PR008) AND A.RQST_DCD = 'CU' AND NOT EXISTS (SELECT 1 FROM WAQ_PDM_TBL D WHERE D.RQST_NO = A.RQST_NO AND D.RQST_SNO = A.RQST_SNO AND ( D.PDM_TBL_PNM = A.PA_TBL_PNM OR D.PDM_TBL_PNM = A.CH_TBL_PNM )
)

mapper.delete:
DELETE /* WaqPdmRelColMapper.deleteByrqstSno */
FROM WAQ_PDM_REL_COL
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
   --신규 적재 대상 ID 업데이트
   UPDATE /* WaqPdmRelColMapper.updateidByKey */
   		WAQ_PDM_REL_COL 
      SET PDM_REL_COL_ID = #{pdmRelColId,jdbcType=VARCHAR}
	  WHERE RQST_NO          = #{rqstNo,jdbcType=VARCHAR}
      AND RQST_SNO         = #{rqstSno,jdbcType=DECIMAL}
      AND RQST_DTL_SNO     = #{rqstDtlSno,jdbcType=DECIMAL}	
      AND RQST_DTL_DTL_SNO = #{rqstDtlDtlSno,jdbcType=DECIMAL}

mapper.select:
SELECT 	/* WaqPdmRelColMapper.selectPdmRelExist */
			A.*
  FROM WAQ_PDM_REL_COL A
 WHERE A.PDM_REL_PNM = #{pdmRelPnm}
   AND A.PA_FULL_PATH = #{paFullPath}
   AND A.PA_TBL_PNM = #{paTblPnm}
   AND A.PA_COL_PNM = #{paColPnm}
   AND A.CH_FULL_PATH = #{chFullPath}
   AND A.CH_TBL_PNM = #{chTblPnm}
   AND A.CH_COL_PNM = #{chColPnm}
   AND A.RQST_NO = #{rqstNo}
   AND A.RQST_SNO = #{rqstSno}

SELECT /* WaqPdmRelColMapper.selectExistsRqstSno */
		MAX(RQST_SNO) AS RQST_SNO
  FROM WAQ_PDM_REL_COL
 WHERE RQST_NO     = #{rqstNo, jdbcType=VARCHAR}
   AND PDM_REL_PNM = #{pdmRelPnm, jdbcType=VARCHAR}
   AND PA_TBL_PNM = #{paTblPnm, jdbcType=VARCHAR}
 GROUP BY PA_TBL_PNM
