mapper.delete:
delete	/* WamSditmMapper.deleteByPrimaryKey */
	from WAM_SDITM
where SDITM_ID = #{sditmId,jdbcType=VARCHAR}

mapper.select:
SELECT * /* WamSditmMapper.selectSditmList */ FROM ( SELECT A.SDITM_ID , A.SDITM_LNM , A.SDITM_PNM , A.SDITM_LNM AS NOTSDITM_LNM , A.SDITM_PNM AS NOTSDITM_PNM , IFNULL(B.ENC_YN,'N') AS STND_INFO_YN ,A.LNM_CRI_DS ,A.DMN_ID ,A.DMNG_ID ,A.INFOTP_ID ,A.INFOTP_CHG_YN ,A.RQST_NO ,A.RQST_SNO ,A.OBJ_DESCN ,A.OBJ_VERS ,A.REG_TYP_CD ,A.FRS_RQST_DTM ,A.FRS_RQST_USER_ID ,A.RQST_DTM ,A.RQST_USER_ID ,A.APRV_DTM ,A.APRV_USER_ID ,A.PNM_CRI_DS ,A.TEST_DATA_CNV_YN ,B.DMN_LNM ,B.DMN_PNM ,D.DATA_TYPE ,D.DATA_LEN ,D.DATA_SCAL ,GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM ,GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM ,GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM ,G.DMNG_LNM ,G.DMNG_PNM ,G.UPP_DMNG_ID ,G.UPP_DMNG_LNM -- ,CONCAT(I.ITM_ENG_MEAN ,'_', DD.DMN_ENG_MEAN) AS FULL_ENG_MEAN -- ,IFNULL(B.ENC_YN,'N') ENC_YN -- ,IFNULL(B.DUP_YN,'N') DUP_YN ,CASE WHEN IFNULL(B.ENC_YN,'N') = 'N' THEN IFNULL(A.ENC_YN, 'N') ELSE IFNULL(B.ENC_YN,'N') END AS ENC_YN ,CASE WHEN IFNULL(B.DUP_YN,'N') = 'N' THEN IFNULL(A.DUP_YN, 'N') ELSE IFNULL(B.DUP_YN,'N') END AS DUP_YN ,A.STND_ASRT ,A.PERS_INFO_CNV_YN ,A.PERS_INFO_GRD ,A.PERS_INFO_YN FROM WAM_SDITM A INNER JOIN WAM_DMN B ON A.DMN_ID = B.DMN_ID -- AND A.INFOTP_ID = B.INFOTP_ID AND A.REG_TYP_CD IN ('C', 'U') AND B.REG_TYP_CD IN ('C', 'U') -- LEFT OUTER JOIN WAA_DMNG_INFOTP_MAP C -- ON A.DMNG_ID = C.DMNG_ID -- AND A.INFOTP_ID = C.INFOTP_ID -- AND A.REG_TYP_CD IN ('C', 'U') -- AND C.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') LEFT OUTER JOIN WAA_INFO_TYPE D ON A.INFOTP_ID = D.INFOTP_ID AND D.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') LEFT OUTER JOIN V_WAA_DMNG G ON A.DMNG_ID = G.DMNG_ID ) SQ
AND SDITM_ID = #{sditmId,jdbcType=VARCHAR}
AND ( SDITM_LNM LIKE CONCAT('%' , #{sditmLnm,jdbcType=VARCHAR} , '%') OR SDITM_PNM LIKE CONCAT('%' , #{sditmLnm,jdbcType=VARCHAR} , '%') OR SDITM_LNM LIKE CONCAT('%' , UPPER(#{sditmLnm,jdbcType=VARCHAR}) , '%') OR SDITM_PNM LIKE CONCAT('%' , UPPER(#{sditmLnm,jdbcType=VARCHAR}) , '%'))
AND ( DMN_LNM LIKE CONCAT('%' , #{dmnLnm,jdbcType=VARCHAR} , '%') OR DMN_PNM LIKE CONCAT('%' , #{dmnLnm,jdbcType=VARCHAR} , '%') OR DMN_LNM LIKE CONCAT('%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%') OR DMN_PNM LIKE CONCAT('%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%'))
AND LNM_CRI_DS = #{lnmCriDs,jdbcType=VARCHAR}
AND DMN_ID = #{dmnId,jdbcType=VARCHAR}
AND DMNG_ID = #{dmngId,jdbcType=VARCHAR}
AND UPP_DMNG_ID = #{uppDmngId,jdbcType=VARCHAR}
AND INFOTP_ID = #{infotpId,jdbcType=VARCHAR}
AND INFOTP_CHG_YN = #{infotpChgYn,jdbcType=VARCHAR}
AND ENC_YN = #{encYn,jdbcType=VARCHAR}
AND DUP_YN = #{dupYn,jdbcType=VARCHAR}
AND RQST_NO = #{rqstNo,jdbcType=VARCHAR}
AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
AND OBJ_DESCN LIKE CONCAT('%' , #{objDescn,jdbcType=VARCHAR} , '%')
AND OBJ_VERS = #{objVers,jdbcType=DECIMAL}
AND REG_TYP_CD = #{regTypCd,jdbcType=VARCHAR}
AND FRS_RQST_DTM = #{frsRqstDtm,jdbcType=TIMESTAMP}
AND FRS_RQST_USER_ID = #{frsRqstUserId,jdbcType=VARCHAR}
AND RQST_DTM = #{rqstDtm,jdbcType=TIMESTAMP}
AND RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
AND APRV_DTM = #{aprvDtm,jdbcType=TIMESTAMP}
AND APRV_USER_ID = #{aprvUserId,jdbcType=VARCHAR}
AND DATA_TYPE = #{dataType,jdbcType=VARCHAR}
AND TEST_DATA_CNV_YN = #{testDataCnvYn,jdbcType=VARCHAR}
AND DATE_FORMAT(APRV_DTM, '%Y-%m-%d') >= #{searchBgnDe}
AND DATE_FORMAT(APRV_DTM, '%Y-%m-%d') <= #{searchEndDe}
AND STND_ASRT = #{stndAsrt}
AND PERS_INFO_CNV_YN= #{persInfoCnvYn}
AND PERS_INFO_GRD = #{persInfoGrd}
AND PERS_INFO_YN= #{persInfoYn}
ORDER BY STND_ASRT, SDITM_LNM

SELECT /* WamSditmMapper.getSditmTransList */ * FROM ( SELECT A.SDITM_ID ,A.SDITM_LNM ,A.SDITM_PNM ,A.LNM_CRI_DS ,A.DMN_ID ,A.DMNG_ID ,A.INFOTP_ID ,A.INFOTP_CHG_YN ,A.RQST_NO ,A.RQST_SNO ,A.OBJ_DESCN ,A.OBJ_VERS ,A.REG_TYP_CD ,A.FRS_RQST_DTM ,A.FRS_RQST_USER_ID ,A.RQST_DTM ,A.RQST_USER_ID ,A.APRV_DTM ,A.APRV_USER_ID ,A.PNM_CRI_DS ,A.TEST_DATA_CNV_YN ,B.DMN_LNM ,B.DMN_PNM ,D.DATA_TYPE ,D.DATA_LEN ,D.DATA_SCAL ,GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM ,GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM ,GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM ,G.DMNG_LNM ,G.DMNG_PNM ,G.UPP_DMNG_ID ,G.UPP_DMNG_LNM ,CONCAT(I.ITM_ENG_MEAN ,'_', DD.DMN_ENG_MEAN) AS FULL_ENG_MEAN
,CASE WHEN IFNULL(B.ENC_YN,'N') = 'N' THEN IFNULL(A.ENC_YN, 'N') ELSE IFNULL(B.ENC_YN,'N') END AS ENC_YN ,CASE WHEN IFNULL(B.DUP_YN,'N') = 'N' THEN IFNULL(A.DUP_YN, 'N') ELSE IFNULL(B.DUP_YN,'N') END AS DUP_YN , IF(A.TRANS_YN ='Y','1','0') AS TRANS_YN , A.STND_ASRT , A.PERS_INFO_CNV_YN , A.PERS_INFO_GRD , A.PERS_INFO_YN FROM WAH_SDITM A INNER JOIN WAM_DMN B ON A.DMN_ID = B.DMN_ID AND A.REG_TYP_CD IN ('C', 'U') AND A.EXP_DTM = STR_TO_DATE('99991231', '%Y%m%d') AND B.REG_TYP_CD IN ('C', 'U') LEFT JOIN WAA_INFO_TYPE D ON A.INFOTP_ID = D.INFOTP_ID AND D.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') LEFT JOIN V_WAA_DMNG G ON A.DMNG_ID = G.DMNG_ID LEFT JOIN (SELECT A.OBJ_ID ,GROUP_CONCAT(B.ENG_MEAN ORDER BY A.WD_SNO SEPARATOR '_') AS ITM_ENG_MEAN FROM WAM_STWD_CNFG A INNER JOIN WAM_STWD B ON A.STWD_ID = B.STWD_ID AND B.REG_TYP_CD IN ('C','U') GROUP BY A.OBJ_ID) I ON A.SDITM_ID = I.OBJ_ID LEFT JOIN (SELECT A.OBJ_ID ,GROUP_CONCAT(B.ENG_MEAN ORDER BY A.WD_SNO SEPARATOR '_') AS DMN_ENG_MEAN FROM WAM_STWD_CNFG A INNER JOIN WAM_STWD B ON A.STWD_ID = B.STWD_ID AND B.REG_TYP_CD IN ('C','U') GROUP BY A.OBJ_ID ) DD ON A.DMN_ID = DD.OBJ_ID
WHERE A.TRANS_YN = #{transYn,jdbcType=VARCHAR}
)
AND SDITM_ID = #{sditmId,jdbcType=VARCHAR}
AND ( SDITM_LNM LIKE CONCAT('%' , #{sditmLnm,jdbcType=VARCHAR} , '%') OR SDITM_PNM LIKE CONCAT('%' , #{sditmLnm,jdbcType=VARCHAR} , '%') OR SDITM_LNM LIKE CONCAT('%' , UPPER(#{sditmLnm,jdbcType=VARCHAR}) , '%') OR SDITM_PNM LIKE CONCAT('%' , UPPER(#{sditmLnm,jdbcType=VARCHAR}) , '%'))
AND ( DMN_LNM LIKE CONCAT('%' , #{dmnLnm,jdbcType=VARCHAR} , '%') OR DMN_PNM LIKE CONCAT('%' , #{dmnLnm,jdbcType=VARCHAR} , '%') OR DMN_LNM LIKE CONCAT('%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%') OR DMN_PNM LIKE CONCAT('%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%'))
AND LNM_CRI_DS = #{lnmCriDs,jdbcType=VARCHAR}
AND DMN_ID = #{dmnId,jdbcType=VARCHAR}
AND DMNG_ID = #{dmngId,jdbcType=VARCHAR}
AND UPP_DMNG_ID = #{uppDmngId,jdbcType=VARCHAR}
AND INFOTP_ID = #{infotpId,jdbcType=VARCHAR}
AND INFOTP_CHG_YN = #{infotpChgYn,jdbcType=VARCHAR}
AND ENC_YN = #{encYn,jdbcType=VARCHAR}
AND DUP_YN = #{dupYn,jdbcType=VARCHAR}
AND RQST_NO = #{rqstNo,jdbcType=VARCHAR}
AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
AND OBJ_DESCN LIKE CONCAT('%' , #{objDescn,jdbcType=VARCHAR} , '%')
AND OBJ_VERS = #{objVers,jdbcType=DECIMAL}
AND REG_TYP_CD = #{regTypCd,jdbcType=VARCHAR}
AND FRS_RQST_DTM = #{frsRqstDtm,jdbcType=TIMESTAMP}
AND FRS_RQST_USER_ID = #{frsRqstUserId,jdbcType=VARCHAR}
AND RQST_DTM = #{rqstDtm,jdbcType=TIMESTAMP}
AND RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
AND APRV_DTM = #{aprvDtm,jdbcType=TIMESTAMP}
AND APRV_USER_ID = #{aprvUserId,jdbcType=VARCHAR}
AND DATA_TYPE = #{dataType,jdbcType=VARCHAR}
AND DATE_FORMAT(APRV_DTM, '%Y-%m-%d') >= #{searchBgnDe}
AND DATE_FORMAT(APRV_DTM, '%Y-%m-%d') <= #{searchEndDe}
AND STND_ASRT = #{stndAsrt}
AND PERS_INFO_CNV_YN= #{persInfoCnvYn}
AND PERS_INFO_GRD = #{persInfoGrd}
AND PERS_INFO_YN= #{persInfoYn}
ORDER BY STND_ASRT, TRANS_YN DESC, SDITM_LNM
