mapper.select:
select /* WaqChnlErrMsgMapper.selectByPrimaryKey */

from WAQ_CHNL_ERR_MSG
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete /* WaqChnlErrMsgMapper.deleteByPrimaryKey */
from WAQ_CHNL_ERR_MSG
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
    SELECT /* WaqChnlErrMsgMapper.selectMsgRqstDetail */
    	A.*
	  FROM WAQ_CHNL_ERR_MSG A
	 WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
	   AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

SELECT /* WaqChnlErrMsgMapper.selectwamlist */ ((SELECT IFNULL(MAX(RQST_SNO), 0) FROM WAQ_CHNL_ERR_MSG WHERE RQST_NO = #{reqmst.rqstNo}) + ROWNUM) AS RQST_SNO , 'I' AS IBS_STATUS , A.CHNL_MSG_ID, A.CHNL_MSG_LNM, A.CHNL_MSG_PNM , #{reqmst.rqstDcd} AS RQST_DCD , A.OBJ_DESCN, A.OBJ_VERS , A.STD_ERR_MSG_ID, A.ERR_CD, A.ERR_TLG_LAN_DSCD, A.ERR_TP_CD, A.CHNL_TP_CD, A.ERR_CD_US_YN, A.CHNL_ERR_CD, A.CHNL_ERR_MSG_CTS, A.ERR_CD_APL_DT, A.ACTN_CD, A.CHNL_DTLS_CLCD, A.CHNL_GRP_CD FROM WAM_CHNL_ERR_MSG A WHERE A.CHNL_MSG_ID IN
#{item.chnlMsgId}
AND A.REG_TYP_CD IN ('C', 'U')

mapper.insert:
/* WaqChnlErrMsgMapper.checkDupMsg */
--검증쿼리 메시지코드 중복 AND EXISTS (SELECT 1 FROM WAQ_CHNL_ERR_MSG D WHERE A.RQST_NO = D.RQST_NO AND A.CHNL_ERR_CD = D.CHNL_ERR_CD
AND A.RQST_SNO != D.RQST_SNO)

mapper.update:
 	UPDATE 	/* WaqChnlErrMsgMapper.updateidByKey */
 			WAQ_CHNL_ERR_MSG SET CHNL_MSG_ID = #{chnlMsgId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
