select /* WaqDmnMapper.selectByPrimaryKey */

from WAQ_DMN
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

SELECT /* WaqDmnMapper.selectByDmnLnm */ A.DMN_LNM, A.DMN_PNM, A.DMNG_ID, A.DMNG_LNM, G.UPP_DMNG_ID, G.UPP_DMNG_LNM, A.INFOTP_ID, A.INFOTP_LNM
FROM WAQ_DMN A LEFT JOIN V_WAA_DMNG G ON A.DMNG_LNM = G.DMNG_LNM AND A.STND_ASRT = G.STND_ASRT AND G.DMNG_LVL IN (SELECT BSC_LVL FROM WAA_BSC_LVL WHERE EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND REG_TYP_CD IN ('C', 'U') AND BSC_ID = 'DMNG' ) WHERE A.DMN_LNM = #{dmnLnm,jdbcType=VARCHAR} AND A.STND_ASRT = #{stndAsrt,jdbcType=VARCHAR} AND A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
UNION ALL SELECT A.DMN_LNM, A.DMN_PNM, A.DMNG_ID, B.DMNG_LNM, G.UPP_DMNG_ID, G.UPP_DMNG_LNM, A.INFOTP_ID, C.INFOTP_LNM
FROM WAM_DMN A INNER JOIN WAA_DMNG B ON A.DMNG_ID = B.DMNG_ID AND A.STND_ASRT = B.STND_ASRT AND B.EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') LEFT JOIN WAA_INFO_TYPE C ON A.INFOTP_ID = C.INFOTP_ID AND A.STND_ASRT = C.STND_ASRT AND C.EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') LEFT JOIN V_WAA_DMNG G ON B.DMNG_LNM = G.DMNG_LNM AND B.STND_ASRT = G.STND_ASRT AND G.DMNG_LVL IN (SELECT BSC_LVL FROM WAA_BSC_LVL WHERE EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND REG_TYP_CD IN ('C', 'U') AND BSC_ID = 'DMNG' ) WHERE A.REG_TYP_CD IN ('C','U') AND A.DMN_LNM = #{dmnLnm,jdbcType=VARCHAR} AND A.STND_ASRT = #{stndAsrt,jdbcType=VARCHAR} AND NOT EXISTS (SELECT 1 FROM WAQ_DMN D WHERE A.DMN_LNM = D.DMN_LNM AND A.STND_ASRT = D.STND_ASRT AND D.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
)


SELECT /* WaqDmnMapper.selectListByCdval */
	A.DMN_LNM, A.DMN_PNM, A.RQST_NO, A.RQST_SNO
  FROM WAQ_DMN A
 WHERE RQST_NO = #{rqstNo}
   AND A.DMN_LNM = #{dmnLnm}
   AND A.STND_ASRT = #{stndAsrt}

SELECT /* WaqDmnMapper.selectListByDmnLnm */ A.DMN_LNM, A.DMN_PNM, A.DMNG_ID, A.DMNG_LNM, G.UPP_DMNG_ID, G.UPP_DMNG_LNM, A.INFOTP_ID, A.INFOTP_LNM, A.DATA_TYPE, A.DATA_LEN, A.DATA_SCAL
FROM WAQ_DMN A LEFT OUTER JOIN V_WAA_DMNG G ON A.DMNG_LNM = G.DMNG_LNM AND A.STND_ASRT = G.STND_ASRT AND G.DMNG_LVL > 0 WHERE A.DMN_LNM = #{dmnLnm,jdbcType=VARCHAR}
AND A.DMN_PNM = #{dmnPnm,jdbcType=VARCHAR}
AND A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
AND A.STND_ASRT =#{stndAsrt} UNION ALL SELECT A.DMN_LNM, A.DMN_PNM, A.DMNG_ID, B.DMNG_LNM, G.UPP_DMNG_ID, G.UPP_DMNG_LNM, A.INFOTP_ID, C.INFOTP_LNM, C.DATA_TYPE, C.DATA_LEN, C.DATA_SCAL FROM WAM_DMN A INNER JOIN WAA_DMNG B ON A.DMNG_ID = B.DMNG_ID AND A.STND_ASRT = B.STND_ASRT AND B.EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') INNER JOIN WAA_INFO_TYPE C ON A.INFOTP_ID = C.INFOTP_ID AND A.STND_ASRT = C.STND_ASRT AND C.EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') LEFT OUTER JOIN V_WAA_DMNG G ON B.DMNG_LNM = G.DMNG_LNM AND B.STND_ASRT = G.STND_ASRT AND G.DMNG_LVL > 0 WHERE A.REG_TYP_CD IN ('C','U') AND A.DMN_LNM = #{dmnLnm,jdbcType=VARCHAR}
AND A.DMN_PNM = #{dmnPnm,jdbcType=VARCHAR}
AND A.STND_ASRT =#{stndAsrt} AND NOT EXISTS (SELECT 1 FROM WAQ_DMN D WHERE A.DMN_LNM = D.DMN_LNM AND A.DMN_PNM = D.DMN_PNM AND A.STND_ASRT = D.STND_ASRT AND D.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
)

