    AND A.CD_VAL_TYP_CD = 'L'
    AND (A.LST_ENTY_PNM IS NULL
     AND A.LST_ENTY_LNM IS NULL
      )


	/* WaqDmnMapper.checkDmnStwdAsm */



    AND A.RQST_DCD = 'CU'
    AND A.DMN_LNM != REPLACE(A.LNM_CRI_DS,';','')


	/* WaqDmnMapper.checkDmnPnmMaxLen */




    AND A.RQST_DCD = 'CU'
    AND LENGTH(A.DMN_PNM) > 30


	/* WaqDmnMapper.checkDmnLnmMaxLen */



    AND A.RQST_DCD = 'CU'
    AND LENGTH(A.DMN_LNM) > 30


	/* WaqDmnMapper.checkDmnPnmStrNum */



    AND A.RQST_DCD = 'CU'
    AND CASE WHEN SUBSTR(IFNULL(A.DMN_PNM,'N'),1,1) BETWEEN '0' AND '9' THEN 'N' ELSE 'C' END = 'N'


	/* WaqDmnMapper.checkCodeLen */



   AND A.RQST_DCD = 'CU'
