               SELECT 1 FROM WAQ_DMN Z
               WHERE Z.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
               AND Z.DATA_LEN != 25
               AND Z.CD_VAL_TYP_CD ='C'
   )

