mapper.insert:
INSERT /* WaqDmnMapper.insertWAM */ INTO WAM_DMN ( DMN_ID
) SELECT DMN_ID
FROM WAQ_DMN A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

INSERT /* WaqDmnMapper.insertWAH */ INTO WAH_DMN ( DMN_ID , EXP_DTM , STR_DTM
) SELECT A.DMN_ID AS DMN_ID , STR_TO_DATE('9999-12-31', '%Y-%m-%d') AS EXP_DTM , NOW() AS STR_DTM
FROM WAQ_DMN A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1'


   	/* WaqDmnMapper.checkDmnPnmExists */



      AND A.RQST_DCD = 'CU'
      AND EXISTS (SELECT 1
			            FROM WAQ_DMN D
			            WHERE D.RQST_NO = #{rqstNo}
			              AND A.DMN_LNM = D.DMN_LNM
			              AND A.STND_ASRT = D.STND_ASRT
			              AND D.DMN_PNM IS NULL

						  AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

			           )


  	/* WaqDmnMapper.checkLNmCriDsExists */



      AND A.RQST_DCD = 'CU'
       AND EXISTS (SELECT 1
			            FROM WAQ_DMN D
			            WHERE D.RQST_NO = #{rqstNo}
			              AND A.DMN_LNM = D.DMN_LNM
			              AND A.STND_ASRT = D.STND_ASRT
			              AND D.LNM_CRI_DS IS NULL

						  AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

			           )


  	/* WaqDmnMapper.checkPNmCriDsExists */



      AND A.RQST_DCD = 'CU'
      AND EXISTS (SELECT 1
			            FROM WAQ_DMN D
			            WHERE D.RQST_NO = #{rqstNo}
			              AND A.DMN_LNM = D.DMN_LNM
			              AND A.STND_ASRT = D.STND_ASRT
			              AND D.PNM_CRI_DS IS NULL

						  AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

			           )

/* WaqDmnMapper.checkCdValExists */
AND A.RQST_DCD = 'CU' AND EXISTS (SELECT 1 FROM WAQ_DMN D INNER JOIN WAA_DMNG G ON D.RQST_NO = #{rqstNo}
AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
AND D.DMNG_ID = G.DMNG_ID AND D.STND_ASRT = G.STND_ASRT AND D.CD_VAL_TYP_CD IN ('O', 'C') AND G.EXP_DTM = STR_TO_DATE('99991231', '%Y%m%d') AND G.CD_DMN_YN ='Y'
LEFT OUTER JOIN WAQ_CD_VAL V ON D.RQST_NO = V.RQST_NO AND D.RQST_SNO = V.RQST_SNO AND V.DMN_LNM = D.DMN_LNM AND V.STND_ASRT = D.STND_ASRT WHERE V.DMN_LNM IS NULL )


   	/* WaqDmnMapper.checkEncYn */



      AND A.RQST_DCD = 'CU'
      AND EXISTS (SELECT 1 FROM WAQ_DMN D
                  WHERE D.RQST_NO = #{rqstNo}

				     AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

                  AND DATA_LEN IN  (64,128,256)
                  AND ENC_YN = 'N'
			    )

