mapper.select:
SELECT /* WaqDmnMapper.selectDmnCdVal */ * FROM( SELECT A.DMN_LNM , A.DMN_PNM , A.LNM_CRI_DS , A.DMNG_ID , A.INFOTP_ID , A.UPP_DMN_ID , A.SUBJ_ID , A.LST_ENTY_ID , A.LST_ENTY_PNM , A.LST_ENTY_LNM , A.LST_ATTR_ID , A.LST_ATTR_PNM , A.LST_ATTR_LNM , A.CD_VAL_TYP_CD , A.CD_VAL_IVW_CD , A.SDITM_AUTO_CRT_YN , A.DATA_FRM , A.CRG_USER_ID , A.DMN_ORG_DS , A.OBJ_DESCN , A.FULL_PATH , A.PNM_CRI_DS , A.ENC_YN , A.DMN_DSCD , A.TRANS_YN , B.DATA_TYPE , B.DATA_LEN , B.DATA_SCAL ,C.CD_VAL_ID, C.CD_VAL , C.CD_VAL_NM, A.DMN_ORG_TXT,A.SUB_CD_YN, A.STND_ASRT FROM WAM_DMN A INNER JOIN V_WAA_DMNG G ON A.DMNG_ID = G.DMNG_ID AND A.STND_ASRT = G.STND_ASRT AND A.REG_TYP_CD IN ('C', 'U') INNER JOIN WAA_INFO_TYPE B ON A.INFOTP_ID = B.INFOTP_ID AND A.STND_ASRT = B.STND_ASRT AND B.EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') INNER JOIN WAM_CD_VAL C ON A.DMN_ID = C.DMN_ID AND A.STND_ASRT = C.STND_ASRT AND C.REG_TYP_CD IN ('C', 'U') UNION SELECT A.DMN_LNM , A.DMN_PNM , A.LNM_CRI_DS , A.DMNG_ID , A.INFOTP_ID , A.UPP_DMN_ID , A.SUBJ_ID , A.LST_ENTY_ID , A.LST_ENTY_PNM , A.LST_ENTY_LNM , A.LST_ATTR_ID , A.LST_ATTR_PNM , A.LST_ATTR_LNM , A.CD_VAL_TYP_CD , A.CD_VAL_IVW_CD , A.SDITM_AUTO_CRT_YN , A.DATA_FRM , A.CRG_USER_ID , A.DMN_ORG_DS , A.OBJ_DESCN , A.FULL_PATH , A.PNM_CRI_DS , A.ENC_YN , A.DMN_DSCD , A.TRANS_YN , B.DATA_TYPE , B.DATA_LEN , B.DATA_SCAL ,C.CD_VAL_ID, C.CD_VAL , C.CD_VAL_NM, A.DMN_ORG_TXT,A.SUB_CD_YN, A.STND_ASRT FROM WAQ_DMN A INNER JOIN V_WAA_DMNG G ON A.DMNG_ID = G.DMNG_ID AND A.STND_ASRT = G.NAT_cd AND A.REG_TYP_CD IN ('C', 'U') INNER JOIN WAA_INFO_TYPE B ON A.INFOTP_ID = B.INFOTP_ID AND A.STND_ASRT = B.STND_ASRT AND B.EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') INNER JOIN WAQ_CD_VAL C ON A.DMN_LNM = C.DMN_LNM AND A.STND_ASRT = C.STND_ASRT AND C.REG_TYP_CD IN ('C', 'U') AND A.RQST_NO = #{rqstNo,jdbcType=VARCHAR} ) DMN
AND (DMN_LNM LIKE '%' || #{dmnLnm,jdbcType=VARCHAR} || '%' OR DMN_PNM LIKE '%' || #{dmnLnm,jdbcType=VARCHAR} || '%' OR DMN_LNM LIKE '%' || UPPER(#{dmnLnm,jdbcType=VARCHAR}) || '%' OR DMN_PNM LIKE '%' || UPPER(#{dmnLnm,jdbcType=VARCHAR}) || '%')
AND DATA_TYPE = #{dataType,jdbcType=VARCHAR}
AND OBJ_DESCN LIKE '%' || #{objDescn,jdbcType=VARCHAR} || '%'
AND STND_ASRT = #{stndAsrt,jdbcType=VARCHAR}
ORDER BY DMN_LNM, CD_VAL

