AND NOT EXISTS ( SELECT 1 FROM WAA_COMM_DCD CD INNER JOIN WAA_COMM_DTL_CD DC ON CD.COMM_DCD_ID = DC.COMM_DCD_ID AND DC.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND DC.REG_TYP_CD IN ('C', 'U') WHERE CD.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND CD.REG_TYP_CD IN ('C', 'U') AND CD.COMM_DCD='PERMIT_COD_DMM_SUFFIX' AND A.DMN_LNM LIKE CONCAT('%',DC.COMM_DTL_CD_NM) )
