mapper.select:

select /* WaqSditmMapper.selectByPrimaryKey */

from WAQ_SDITM
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:

DELETE /* WaqSditmMapper.deleteByPrimaryKey */
FROM WAQ_SDITM
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}


DELETE A /* WaqSditmMapper.deleteByDmnSno */
FROM WAQ_SDITM A
 WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
   AND EXISTS (SELECT 1 FROM WAQ_DMN B
              WHERE B.RQST_NO   = #{rqstNo,jdbcType=VARCHAR}
                AND B.RQST_SNO  = #{rqstSno,jdbcType=DECIMAL}
                AND A.SDITM_LNM = B.DMN_LNM
                AND A.STND_ASRT = B.STND_ASRT
                AND A.RQST_NO   = B.RQST_NO)

mapper.insert:
/* WaqSditmMapper.checkDupSditm */
-- 검증쿼리 AND EXISTS (SELECT 1 FROM WAQ_SDITM D WHERE A.RQST_NO = D.RQST_NO AND A.SDITM_LNM = D.SDITM_LNM and A.STND_ASRT = D.STND_ASRT
AND A.RQST_SNO != D.RQST_SNO)

mapper.update:

 	UPDATE 	/* WaqSditmMapper.updateidByKey */
 			WAQ_SDITM SET SDITM_ID = #{sditmId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.insert:
-- WAM 추가 (WAQ 내용을 추가, REG_TYP_CD 가 신규,변경일 경우) INSERT /* WaqSditmMapper.insertWAM */ INTO WAM_SDITM ( SDITM_ID
) SELECT A.SDITM_ID
FROM WAQ_SDITM A LEFT JOIN WAM_DMN B ON A.DMN_ID = B.DMN_ID AND A.STND_ASRT = B.STND_ASRT WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

-- WAH이력 적재... INSERT /* WaqSditmMapper.insertWAH */ INTO WAH_SDITM ( SDITM_ID , EXP_DTM , STR_DTM
) SELECT A.SDITM_ID , STR_TO_DATE('9999-12-31', '%Y-%m-%d') AS EXP_DTM , NOW() AS STR_DTM
FROM WAQ_SDITM A LEFT JOIN WAM_DMN B ON B.DMN_ID = A.DMN_ID AND B.STND_ASRT = A.STND_ASRT WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1'

/* WaqSditmMapper.checkObjDesn */
-- 검증쿼리 AND EXISTS (SELECT 1 FROM WAQ_SDITM D WHERE A.RQST_NO = D.RQST_NO AND A.SDITM_LNM = D.SDITM_LNM AND A.STND_ASRT = D.STND_ASRT
AND D.OBJ_DESCN IS NULL )
