mapper.insert:
INSERT /* WaqStwdCnfgMapper.insertwam2waq */ INTO WAQ_STWD_CNFG SELECT #{reqmst.bizDtlCd}, X.RQST_NO, X.RQST_SNO, B.WD_SNO, B.STWD_ID, C.STWD_LNM, C.STWD_PNM, X.RQST_DCD , NULL, NULL , X.STND_ASRT FROM WAQ_DMN X JOIN WAM_DMN A ON X.DMN_ID = A.DMN_ID AND X.STND_ASRT = A.STND_ASRT JOIN WAM_STWD_CNFG B ON A.DMN_ID = B.OBJ_ID AND A.STND_ASRT = B.STND_ASRT JOIN WAM_STWD C ON B.STWD_ID = C.STWD_ID AND B.STND_ASRT = C.STND_ASRT AND C.REG_TYP_CD IN ('C', 'U') WHERE X.RQST_NO = #{reqmst.rqstNo} AND A.DMN_ID IN -- ('OBJ_00000001173')
#{item.dmnId}
AND A.REG_TYP_CD IN ('C', 'U')

INSERT /* WaqStwdCnfgMapper.insertWAM */ INTO WAM_STWD_CNFG ( OBJ_ID , WD_SNO , STWD_ID , RQST_NO , RQST_SNO
) SELECT A.DMN_ID , B.WD_SNO , C.STWD_ID , A.RQST_NO , A.RQST_SNO
FROM WAQ_DMN A INNER JOIN WAQ_STWD_CNFG B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO AND B.BIZ_DTL_CD = 'DMN' INNER JOIN WAM_STWD C ON B.STWD_LNM = C.STWD_LNM AND B.STWD_PNM = C.STWD_PNM AND B.STND_ASRT = C.STND_ASRT AND C.REG_TYP_CD IN ('C', 'U') WHERE A.RQST_DCD = 'CU' AND A.RVW_STS_CD = '1' AND A.RQST_NO = #{rqstNo}

INSERT /* WaqStwdCnfgMapper.insertWAMItem */ INTO WAM_STWD_CNFG ( OBJ_ID , WD_SNO , STWD_ID , RQST_NO , RQST_SNO
) SELECT A.SDITM_ID , B.WD_SNO , C.STWD_ID , A.RQST_NO , A.RQST_SNO
FROM WAQ_SDITM A INNER JOIN WAQ_STWD_CNFG B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO AND B.BIZ_DTL_CD = 'SDITM' INNER JOIN WAM_STWD C ON B.STWD_LNM = C.STWD_LNM AND B.STWD_PNM = C.STWD_PNM AND B.STND_ASRT = C.STND_ASRT AND C.REG_TYP_CD IN ('C', 'U') WHERE A.RQST_DCD = 'CU' AND A.RVW_STS_CD = '1' AND A.RQST_NO = #{rqstNo}

