mapper.update:
UPDATE WAA_APRG
SET REG_TYP_CD = #{regTypCd}
where APRG_ID = #{aprgId,jdbcType=VARCHAR}

mapper.select:
SELECT A.* ,GET_USER_NM(A.WRIT_USER_ID) AS WRIT_USER_NM FROM WAA_APRG A WHERE A.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND A.REG_TYP_CD IN ('C', 'U')
AND APRG_ID = #{aprgId,jdbcType=VARCHAR}
ORDER BY APRG_NM
