mapper.select:
SELECT A.DMNG_ID ,A.EXP_DTM ,A.STR_DTM ,A.DMNG_LNM ,A.DMNG_PNM ,A.UPP_DMNG_ID ,B.DMNG_LNM AS UPP_DMNG_LNM ,A.DMNG_LVL ,A.CD_DMN_YN ,A.INFOTP_CHG_CAN_YN ,A.OBJ_DESCN ,A.OBJ_VERS ,A.WRIT_DTM ,A.WRIT_USER_ID ,A.FULL_PATH ,GET_USER_NM(A.WRIT_USER_ID) AS WRIT_USER_NM ,A.STND_ASRT FROM (
) A LEFT OUTER JOIN WAA_DMNG B ON A.UPP_DMNG_ID = B.DMNG_ID AND B.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d')
AND (A.DMNG_LNM LIKE CONCAT('%' , #{dmngLnm,jdbcType=VARCHAR} , '%') OR A.DMNG_PNM LIKE CONCAT('%' , #{dmngLnm,jdbcType=VARCHAR} , '%'))
AND A.DMNG_ID = #{dmngId,jdbcType=VARCHAR}
AND A.STND_ASRT = #{stndAsrt,jdbcType=VARCHAR}
ORDER BY A.STND_ASRT,A.ORDER_COLUMN,A.DMNG_LVL

SELECT *
  FROM WAA_DMNG
 WHERE EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d')
   AND DMNG_LNM = #{dmngLnm,jdbcType=VARCHAR}
   AND DMNG_LVL = 0
   AND STND_ASRT = #{stndAsrt,jdbcType=VARCHAR}

SELECT *
  FROM WAA_DMNG
 WHERE EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d')
   AND STND_ASRT = #{stndAsrt,jdbcType=VARCHAR}
   AND DMNG_LNM  = #{dmngLnm,jdbcType=VARCHAR}
   AND DMNG_LVL  = #{dmngLvl}

   AND IFNULL(UPP_DMNG_LNM,'_') = IFNULL(#{uppDmngLnm},'_')
