mapper.select:
select 

,

from WAQ_BR_MSTR
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_BR_MSTR
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
	UPDATE WAQ_BR_MSTR SET BR_ID = #{brId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.insert:
--WAM 추가 (WAQ 내용을 추가, REG_TYP_CD 가 신규,변경일 경우) INSERT INTO WAM_BR_MSTR ( BR_ID
) SELECT BR_ID
FROM WAQ_BR_MSTR A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

--WAH이력 적재... INSERT INTO WAH_BR_MSTR ( BR_ID , EXP_DTM , STR_DTM
) SELECT BR_ID , STR_TO_DATE( '9999-12-31', '%Y-%m-%d') AS EXP_DTM , SYSDATE AS STR_DTM
FROM WAQ_BR_MSTR WHERE RQST_NO = #{rqstNo} AND RVW_STS_CD = '1'

mapper.delete:
delete from WAQ_RQST_VRF_DTLS
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=VARCHAR}
  and VRF_DTL_CD = #{vrfDtlCd,jdbcType=VARCHAR}

delete from WAQ_RQST_VRF_DTLS
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND VRF_DTL_CD != #{vrfDtlCd,jdbcType=VARCHAR}
