mapper.select:
SELECT /* WaqCodeCfcSysMapper.selectByPrimaryKey */
	A.*
  FROM WAQ_CODE_CFC_SYS A

 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
   AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete /* WaqCodeCfcSysMapper.deleteByPrimaryKey */
from WAQ_CODE_CFC_SYS
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
   --신규 적재 대상 ID 업데이트
   UPDATE /* WaqCodeCfcSysMapper.updateidByKey */
   		WAQ_CODE_CFC_SYS SET CODE_CFC_SYS_ID = #{codeCfcSysId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
--코드분류체계 변경대상 조회... SELECT /* WaqCodeCfcSysMapper.selectwamlist */ ((SELECT IFNULL(MAX(RQST_SNO), 0) FROM WAQ_CODE_CFC_SYS WHERE RQST_NO = #{reqmst.rqstNo}) + ROWNUM) AS RQST_SNO , 'I' AS IBS_STATUS , A.CODE_CFC_SYS_ID, A.CODE_CFC_SYS_CD, A.CODE_CFC_SYS_LNM, A.CODE_CFC_SYS_PNM, 'CU' AS RQST_DCD , A.CODE_CFC_SYS_FRM , A.CRG_USER_ID, B.USER_NM AS CRG_USER_NM, A.OBJ_DESCN, A.OBJ_VERS FROM WAM_CODE_CFC_SYS A LEFT OUTER JOIN WAA_USER B ON A.CRG_USER_ID = B.USER_ID AND B.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') AND B.REG_TYP_CD IN ('C', 'U') WHERE A.CODE_CFC_SYS_ID IN
#{item.codeCfcSysId}
AND A.REG_TYP_CD IN ('C', 'U')

mapper.insert:
/* WaqCodeCfcSysMapper.checkNotChgData */
--검증쿼리 : 변경일 때 변경된 데이터가 없을 경우 (CCS00) AND A.REG_TYP_CD = 'U' AND EXISTS ( SELECT 1 FROM WAM_CODE_CFC_SYS D WHERE A.CODE_CFC_SYS_CD = D.CODE_CFC_SYS_CD AND A.CODE_CFC_SYS_LNM = D.CODE_CFC_SYS_LNM AND A.CODE_CFC_SYS_FRM = D.CODE_CFC_SYS_FRM AND IFNULL(A.CRG_USER_ID, '') = IFNULL(D.CRG_USER_ID, '') AND IFNULL(A.OBJ_DESCN, '') = IFNULL(D.OBJ_DESCN, '')
)
