mapper.select:
select /* WaqPdmColMapper.selectPdmColDetail */

from WAQ_PDM_COL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

SELECT /* WaqPdmColMapper.selectPdmColRqstList */
, CASE WHEN A.VRF_CD = '2' THEN '#FF0000' ELSE CASE WHEN A.VRF_CD = '5' THEN '#0000FF' END END AS FontColor , U.USER_NM AS RQST_USER_NM , A.ENC_YN FROM WAQ_PDM_COL A JOIN WAQ_PDM_TBL B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO LEFT OUTER JOIN WAA_USER U ON A.RQST_USER_ID = U.USER_ID AND U.REG_TYP_CD IN ('C', 'U') AND U.EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d') WHERE A.RQST_NO = #{rqstNo}
AND A.RQST_SNO = #{rqstSno}
ORDER BY A.RQST_SNO, A.COL_ORD

mapper.delete:
    delete /* WaqPdmColMapper.deleteByPrimaryKey */
	from WAQ_PDM_COL
    where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
      and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
      and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

    DELETE	/* WaqPdmColMapper.deleteByrqstSno */
	FROM WAQ_PDM_COL
    WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
      AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.insert:
AND A.RQST_DCD = 'DD' AND NOT EXISTS (SELECT 1 FROM WAM_PDM_COL D INNER JOIN WAM_PDM_TBL E ON E.PDM_TBL_ID = D.PDM_TBL_ID AND E.REG_TYP_CD IN ('C','U') INNER JOIN WAQ_PDM_TBL F ON F.FULL_PATH = E.FULL_PATH AND F.PDM_TBL_PNM = E.PDM_TBL_PNM
WHERE F.RQST_NO = A.RQST_NO AND F.RQST_SNO = A.RQST_SNO AND D.PDM_COL_PNM = A.PDM_COL_PNM
)

mapper.update:
     UPDATE /* WaqPdmColMapper.updateidByKey */
		WAQ_PDM_COL 
        SET PDM_COL_ID = #{pdmColId,jdbcType=VARCHAR}
  	  WHERE RQST_NO       = #{rqstNo,jdbcType=VARCHAR}
        AND RQST_SNO     = #{rqstSno,jdbcType=DECIMAL}
        AND RQST_DTL_SNO = #{rqstDtlSno}
