select	/* WamMtaTblMapper.selectByPrimaryKey */

from WAM_MTA_TBL
where MTA_TBL_ID = #{mtaTblId,jdbcType=VARCHAR}

SELECT /* WamMtaTblMapper.selectList */ A.* , (SELECT USER_NM FROM WAA_USER B1 WHERE B1.USER_ID = A.FRS_RQST_USER_ID AND B1.EXP_DTM = STR_TO_DATE('99991231', '%Y%m%d')) AS FRS_RQST_USER_NM , (SELECT USER_NM FROM WAA_USER B1 WHERE B1.USER_ID = A.RQST_USER_ID AND B1.EXP_DTM = STR_TO_DATE('99991231', '%Y%m%d')) AS RQST_USER_NM , (SELECT USER_NM FROM WAA_USER B1 WHERE B1.USER_ID = A.APRV_USER_ID AND B1.EXP_DTM = STR_TO_DATE('99991231', '%Y%m%d')) AS APRV_USER_NM , (SELECT DBMS_TYP_CD FROM WAA_DB_CONN_TRG WHERE EXP_DTM = STR_TO_DATE('99991231', '%Y%m%d') AND REG_TYP_CD IN ('C','U') AND DB_CONN_TRG_ID = A.DB_CONN_TRG_ID ) AS DBMS_TYP_CD FROM WAM_MTA_TBL A WHERE 1 = 1 AND A.GAP_STS_CD = 'S' AND A.REG_TYP_CD IN ('C', 'U')
AND A.MTA_TBL_ID = #{mtaTblId,jdbcType=VARCHAR}
AND ( UPPER(A.MTA_TBL_PNM) LIKE '%' || UPPER(#{mtaTblLnm,jdbcType=VARCHAR})||'%' OR A.MTA_TBL_PNM LIKE '%' || #{mtaTblLnm,jdbcType=VARCHAR} || '%' OR UPPER(A.MTA_TBL_LNM) LIKE '%' || UPPER(#{mtaTblLnm,jdbcType=VARCHAR})||'%' OR A.MTA_TBL_LNM LIKE '%' || #{mtaTblLnm, jdbcType=VARCHAR} || '%')
AND A.ORG_CD in ( SELECT ORG_CD FROM WAA_ORG START WITH ORG_CD = #{orgCd,jdbcType=VARCHAR} CONNECT BY PRIOR ORG_CD = UPP_ORG_CD AND REG_TYP_CD IN ('C','U') AND EXP_DTM = STR_TO_DATE('9999-12-31', '%Y-%m-%d'))
AND A.ORG_CD IN ( SELECT ORG_CD FROM WAA_ORG WHERE REG_TYP_CD IN ('C','U') AND EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') AND ORG_NM LIKE '%' || #{orgNm,jdbcType=VARCHAR} || '%' )
AND A.INFO_SYS_CD = #{infoSysCd,jdbcType=VARCHAR}
AND A.INFO_SYS_CD IN ( SELECT INFO_SYS_CD FROM WAA_INFO_SYS WHERE EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') AND UPPER(INFO_SYS_NM) LIKE '%' || UPPER(#{infoSysNm, jdbcType=VARCHAR}) || '%')
AND A.DB_CONN_TRG_ID = #{dbConnTrgId,jdbcType=VARCHAR}
AND A.DB_CONN_TRG_ID IN ( SELECT DB_CONN_TRG_ID FROM WAA_DB_CONN_TRG WHERE EXP_DTM = STR_TO_DATE('99991231','%Y%m%d') AND REG_TYP_CD IN ('C','U') AND ( DB_CONN_TRG_PNM LIKE '%' || UPPER(#{dbConnTrgPnm,jdbcType=VARCHAR}) || '%' OR DB_CONN_TRG_PNM LIKE '%' || #{dbConnTrgPnm,jdbcType=VARCHAR} || '%' OR DB_CONN_TRG_LNM LIKE '%' || UPPER(#{dbConnTrgPnm,jdbcType=VARCHAR}) || '%' OR DB_CONN_TRG_LNM LIKE '%' || #{dbConnTrgPnm,jdbcType=VARCHAR} || '%' ))
AND A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
ORDER BY A.MTA_TBL_PNM

SELECT /* WamMtaTblMapper.selectListByRqstNo */ A.MTA_TBL_ID , IFNULL(A.MTA_TBL_PNM , '') MTA_TBL_PNM , IFNULL(A.MTA_TBL_LNM , '') MTA_TBL_LNM , IFNULL(A.ORG_CD , '') ORG_CD , IFNULL(A.INFO_SYS_CD , '') INFO_SYS_CD , IFNULL(A.DB_CONN_TRG_ID , '') DB_CONN_TRG_ID , IFNULL(A.DB_CONN_TRG_PNM , '') DB_CONN_TRG_PNM , IFNULL(A.DB_SCH_ID , '') DB_SCH_ID , IFNULL(A.DB_SCH_PNM , '') DB_SCH_PNM , IFNULL(A.OBJ_DESCN , '') OBJ_DESCN , IFNULL(A.OBJ_VERS , '') OBJ_VERS , IFNULL(A.REG_TYP_CD , '') REG_TYP_CD , IFNULL(TO_CHAR(A.FRS_RQST_DTM,'YYYYMMDDHH24MISS'),'') FRS_RQST_DTM , IFNULL(A.FRS_RQST_USER_ID, '') FRS_RQST_USER_ID , IFNULL(A.RQST_NO , '') RQST_NO , IFNULL(A.RQST_SNO , '') RQST_SNO , IFNULL(TO_CHAR(A.RQST_DTM,'YYYYMMDDHH24MISS'),'') RQST_DTM , IFNULL(A.RQST_USER_ID , '') RQST_USER_ID , IFNULL(TO_CHAR(A.APRV_DTM,'YYYYMMDDHH24MISS'),'') APRV_DTM , IFNULL(A.APRV_USER_ID , '') APRV_USER_ID , IFNULL(A.TBL_TYP_NM , '') TBL_TYP_NM , IFNULL(A.REL_ENTY_NM , '') REL_ENTY_NM , IFNULL(A.SUBJ_NM , '') SUBJ_NM , IFNULL(A.TAG_INF_NM , '') TAG_INF_NM , IFNULL(A.PRSV_TERM , '') PRSV_TERM , IFNULL(A.TBL_VOL , '') TBL_VOL , IFNULL(A.EXPT_OCCR_CNT , '') EXPT_OCCR_CNT , IFNULL(A.TBL_CRE_DT , '') TBL_CRE_DT , IFNULL(A.NOPEN_RSN , '') NOPEN_RSN , IFNULL(A.OPEN_DATA_LST , '') OPEN_DATA_LST , IFNULL(A.OCCR_CYL , '') OCCR_CYL , IFNULL(A.DQ_DGNS_YN , 'N') DQ_DGNS_YN , IFNULL(A.TBL_CLLT_DCD , 'A') TBL_CLLT_DCD , IFNULL(A.OPEN_RSN_CD , '') OPEN_RSN_CD , IFNULL(A.NOPEN_DTL_REL_BSS, '') NOPEN_DTL_REL_BSS FROM WAQ_MTA_TBL A WHERE 1 = 1 AND A.RVW_STS_CD = '1'
AND A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
ORDER BY A.MTA_TBL_PNM

SELECT /* WamMtaTblMapper.selectListByTblId */ A.MTA_TBL_ID , IFNULL(A.MTA_TBL_PNM , '') MTA_TBL_PNM , IFNULL(A.MTA_TBL_LNM , '') MTA_TBL_LNM , IFNULL(A.ORG_CD , '') ORG_CD , IFNULL(A.INFO_SYS_CD , '') INFO_SYS_CD , IFNULL(A.DB_CONN_TRG_ID , '') DB_CONN_TRG_ID , IFNULL(A.DB_CONN_TRG_PNM , '') DB_CONN_TRG_PNM , IFNULL(A.DB_SCH_ID , '') DB_SCH_ID , IFNULL(A.DB_SCH_PNM , '') DB_SCH_PNM , IFNULL(A.OBJ_DESCN , '') OBJ_DESCN , IFNULL(A.OBJ_VERS , '') OBJ_VERS , IFNULL(A.REG_TYP_CD , '') REG_TYP_CD , IFNULL(TO_CHAR(A.FRS_RQST_DTM,'YYYYMMDDHH24MISS'),'') FRS_RQST_DTM , IFNULL(A.FRS_RQST_USER_ID, '') FRS_RQST_USER_ID , IFNULL(A.RQST_NO , '') RQST_NO , IFNULL(A.RQST_SNO , '') RQST_SNO , IFNULL(TO_CHAR(A.RQST_DTM,'YYYYMMDDHH24MISS'),'') RQST_DTM , IFNULL(A.RQST_USER_ID , '') RQST_USER_ID , IFNULL(TO_CHAR(A.APRV_DTM,'YYYYMMDDHH24MISS'),'') APRV_DTM , IFNULL(A.APRV_USER_ID , '') APRV_USER_ID , IFNULL(A.TBL_TYP_NM , '') TBL_TYP_NM , IFNULL(A.REL_ENTY_NM , '') REL_ENTY_NM , IFNULL(A.SUBJ_NM , '') SUBJ_NM , IFNULL(A.TAG_INF_NM , '') TAG_INF_NM , IFNULL(A.PRSV_TERM , '') PRSV_TERM , IFNULL(A.TBL_VOL , '') TBL_VOL , IFNULL(A.EXPT_OCCR_CNT , '') EXPT_OCCR_CNT , IFNULL(A.TBL_CRE_DT , '') TBL_CRE_DT , IFNULL(A.NOPEN_RSN , '') NOPEN_RSN , IFNULL(A.OPEN_DATA_LST , '') OPEN_DATA_LST , IFNULL(A.OCCR_CYL , '') OCCR_CYL , IFNULL(A.DQ_DGNS_YN , 'N') DQ_DGNS_YN , IFNULL(A.TBL_CLLT_DCD , 'A') TBL_CLLT_DCD , IFNULL(A.OPEN_RSN_CD , '') OPEN_RSN_CD , IFNULL(A.NOPEN_DTL_REL_BSS, '') NOPEN_DTL_REL_BSS FROM WAM_MTA_TBL A WHERE 1 = 1
AND A.MTA_TBL_ID = #{mtaTblId,jdbcType=VARCHAR}
ORDER BY A.MTA_TBL_PNM
