mapper.select:

	SELECT /* WamStwdCnfgMapper.selectByPrimaryKey */

	FROM
		WAM_STWD_CNFG
	WHERE OBJ_ID = #{objId,jdbcType=VARCHAR}
  		AND WD_SNO = #{wdSno,jdbcType=DECIMAL}

mapper.delete:

DELETE /* WamStwdCnfgMapper.deleteByPrimaryKey */
FROM
	WAM_STWD_CNFG
WHERE OBJ_ID = #{objId,jdbcType=VARCHAR}
	AND WD_SNO = #{wdSno,jdbcType=DECIMAL}

mapper.update:

UPDATE /* WamStwdCnfgMapper.updateByPrimaryKey */
	WAM_STWD_CNFG
SET
	STWD_ID = #{stwdId,jdbcType=VARCHAR},
	RQST_NO = #{rqstNo,jdbcType=VARCHAR},
	RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
WHERE OBJ_ID = #{objId,jdbcType=VARCHAR}
	AND WD_SNO = #{wdSno,jdbcType=DECIMAL}

mapper.select:
SELECT /* WamStwdCnfgMapper.selectSditmInitList */ A.OBJ_ID ,A.WD_SNO ,A.STWD_ID ,A.RQST_NO ,A.RQST_SNO ,'STWD' AS BIZ_DTL_CD ,C.STWD_LNM ,C.STWD_PNM ,C.ENG_MEAN ,C.OBJ_DESCN ,B.STND_ASRT FROM WAM_STWD_CNFG A INNER JOIN WAM_SDITM B ON A.OBJ_ID = B.SDITM_ID INNER JOIN WAM_STWD C ON A.STWD_ID = C.STWD_ID WHERE SDITM_ID = #{sditmId,jdbcType=VARCHAR}
UNION ALL
SELECT A.DMN_ID AS OBJ_ID ,9999 AS WD_SNO ,A.DMN_ID AS STWD_ID ,A.RQST_NO AS RQST_NO ,A.RQST_SNO AS RQST_SNO ,'DMN' AS BIZ_DTL_CD ,A.DMN_LNM AS STWD_LNM ,A.DMN_PNM AS STWD_PNM ,'' AS ENG_MEAN ,A.OBJ_DESCN AS OBJ_DESCN ,B.STND_ASRT FROM WAM_DMN A INNER JOIN WAM_SDITM B ON A.DMN_ID = B.DMN_ID AND A.DMNG_ID = B.DMNG_ID WHERE 1=1 AND B.SDITM_ID = #{sditmId,jdbcType=VARCHAR}
