mapper.select:
select /* WaqStdErrMsgMapper.selectByPrimaryKey */

from WAQ_STD_ERR_MSG
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete /* WaqStdErrMsgMapper.deleteByPrimaryKey */
from WAQ_STD_ERR_MSG
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
    SELECT /* WaqStdErrMsgMapper.selectMsgRqstDetail */
    	A.*
	  FROM WAQ_STD_ERR_MSG A
	 WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
	   AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

SELECT /* WaqStdErrMsgMapper.selectwamlist */ ((SELECT IFNULL(MAX(RQST_SNO), 0) FROM WAQ_STD_ERR_MSG WHERE RQST_NO = #{reqmst.rqstNo}) + ROWNUM) AS RQST_SNO , 'I' AS IBS_STATUS , A.STD_ERR_MSG_ID, A.STD_ERR_MSG_LNM, A.STD_ERR_MSG_PNM , #{reqmst.rqstDcd} AS RQST_DCD , A.OBJ_DESCN, A.OBJ_VERS , A.ERR_TLG_LAN_DSCD, A.ERR_TP_CD, A.MSG_DIT , A.STD_ERR_CD_YN, A.ACTN_CD, ERR_CD, A.ISD_ERR_CAS_CTS, A.ISD_ERR_ACTN_CTS, A.OSD_ERR_CAS_CTS , A.OSD_ERR_ACTN_CTS, A.ERR_CD_APL_DT, A.STSY_ERR_CD, A.TSK_DMN, A.MNG_USER, A.BIZ_DIT, A.MSG_TYP FROM WAM_STD_ERR_MSG A WHERE A.STD_ERR_MSG_ID IN
#{item.stdErrMsgId}
AND A.REG_TYP_CD IN ('C', 'U')

mapper.insert:
/* WaqStdErrMsgMapper.checkDupMsg */
--검증쿼리 메시지코드 중복 AND EXISTS (SELECT 1 FROM WAQ_STD_ERR_MSG D WHERE A.RQST_NO = D.RQST_NO AND A.ERR_CD = D.ERR_CD
AND A.RQST_SNO != D.RQST_SNO)

mapper.update:
 	UPDATE 	/* WaqStdErrMsgMapper.updateidByKey */
 			WAQ_STD_ERR_MSG SET STD_ERR_MSG_ID = #{stdErrMsgId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
