mapper.select:

SELECT /* WaqStwdMapper.selectRqstStndWord */

FROM WAQ_STWD
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}


    SELECT /* WaqStwdMapper.selectListByStwdLnm */
    	A.STWD_LNM, 
    	A.STWD_PNM
	FROM WAM_STWD A
    WHERE A.STWD_LNM = #{stwdLnm,jdbcType=VARCHAR}
      AND A.REG_TYP_CD in ('C','U')

      AND A.STND_ASRT = #{stndAsrt, jdbcType=VARCHAR} 

      AND NOT EXISTS (SELECT 1 FROM WAQ_STWD B
                       WHERE B.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
                         AND A.STWD_LNM = B.STWD_LNM
                         AND A.STWD_PNM = B.STWD_LNM
                         AND A.STND_ASRT = B.STND_ASRT
                         )
    UNION ALL
    SELECT STWD_LNM, STWD_PNM
      FROM WAQ_STWD
     WHERE RQST_NO  = #{rqstNo,jdbcType=VARCHAR}
       AND STWD_LNM = #{stwdLnm,jdbcType=VARCHAR}

       AND STND_ASRT = #{stndAsrt, jdbcType=VARCHAR} 

       AND RQST_DCD = 'CU'

mapper.delete:

