mapper.update:

UPDATE /* WaqStwdMapper.updateByPrimaryKey */
	WAQ_STWD
  SET STWD_LNM     = REPLACE(#{stwdLnm,jdbcType=VARCHAR},' ','')
    , STWD_PNM     = UPPER(REPLACE(#{stwdPnm,jdbcType=VARCHAR},' ',''))
    , RQST_DCD     = #{rqstDcd,jdbcType=VARCHAR}
    , ENG_MEAN     = #{engMean,jdbcType=VARCHAR}
    , CCH_NM       = #{cchNm,jdbcType=VARCHAR}
    , ORG_DS       = #{orgDs,jdbcType=VARCHAR}
    , OBJ_DESCN    = #{objDescn,jdbcType=VARCHAR}
    , WD_DCD       = #{wdDcd,jdbcType=VARCHAR}
    , RQST_DTM     = NOW()
    , RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
    , DMN_YN       =  #{dmnYn,jdbcType=VARCHAR}
    , STND_ASRT    = #{stndAsrt,jdbcType=VARCHAR}

WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}


	UPDATE 	/* WaqStwdMapper.updateidByKey */
			WAQ_STWD 
	   SET STWD_ID = #{stwdId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.insert:
/* WaqStwdMapper.checkExistSymn */
AND A.RQST_DCD = 'CU' AND EXISTS (
SELECT 1 FROM WAM_SYMN D WHERE D.SYMN_LNM = A.STWD_LNM AND A.STND_ASRT = D.STND_ASRT AND D.SYMN_DCD = 'RV' AND D.REG_TYP_CD IN ('C','U') )


	/* WaqStwdMapper.checkLnmExistSpac */




    AND A.RQST_DCD = 'CU'
    AND INSTR(A.STWD_LNM, ' ') > 0


	/* WaqStwdMapper.checkPnmExistSpac */




    AND A.RQST_DCD = 'CU'
    AND INSTR(A.STWD_PNM, ' ') > 0


	/* WaqStwdMapper.checkLnmMaxLen */




    AND A.RQST_DCD = 'CU'
    AND CHAR_LENGTH(A.STWD_LNM) > 30


	/* WaqStwdMapper.checkPnmMaxLen */




    AND A.RQST_DCD = 'CU'
    AND CHAR_LENGTH(A.STWD_PNM) > 30

/* WaqStwdMapper.checkHomonym */
AND A.REG_TYP_CD = 'C' AND EXISTS (SELECT 1 FROM WAM_STWD D WHERE A.STWD_LNM = D.STWD_LNM AND A.STWD_PNM != D.STWD_PNM AND A.STND_ASRT = D.STND_ASRT )
AND NOT EXISTS( SELECT 1 FROM WAM_SYMN F WHERE A.STWD_LNM = F.SYMN_LNM AND A.STWD_PNM = F.SYMN_PNM AND A.STND_ASRT = F.STND_ASRT AND F.SYMN_DCD = 'HM' )

