FROM WAQ_STWD A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

INSERT /* WaqStwdMapper.insertWAH */ INTO WAH_STWD ( STWD_ID , EXP_DTM , STR_DTM
) SELECT A.STWD_ID AS STWD_ID , STR_TO_DATE('9999-12-31', '%Y-%m-%d') AS EXP_DTM , NOW() AS STR_DTM
FROM WAQ_STWD A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1'


