mapper.update:
UPDATE WAA_COL_RULE_REL
   SET UPDT_DTM = SYSDATE
     , VRFC_ID = #{vrfcId,jdbcType=VARCHAR}       

     ,  CD_CLS_ID = #{cdClsId,jdbcType=VARCHAR}            
     ,  VRFC_NM   = #{vrfcNm,jdbcType=VARCHAR}            
 WHERE DB_SCH_ID   = #{dbSchId,jdbcType=VARCHAR}
   AND DBC_TBL_NM  = #{dbcTblNm,jdbcType=VARCHAR}
   AND DBC_COL_NM  = #{dbcColNm,jdbcType=VARCHAR}

mapper.select:
SELECT C.DB_CONN_TRG_PNM , B.DB_SCH_ID , B.DB_SCH_PNM , A.DBC_TBL_NM , A.DBC_TBL_KOR_NM , CASE WHEN D.EXP_YN = 'Y' THEN '1' ELSE '0' END AS EXP_YN , D.EXP_RSN_CNTN FROM WAT_DBC_TBL A INNER JOIN WAA_DB_SCH B ON B.EXP_DTM = TO_DATE( '99991231', 'YYYYMMDD') AND B.DB_SCH_ID = A.DB_SCH_ID INNER JOIN WAA_DB_CONN_TRG C ON C.EXP_DTM = TO_DATE( '99991231', 'YYYYMMDD') AND C.DB_CONN_TRG_ID = B.DB_CONN_TRG_ID LEFT JOIN WAA_EXP_TBL D ON D.DB_SCH_ID = A.DB_SCH_ID AND D.DBC_TBL_NM = A.DBC_TBL_NM WHERE 1 = 1 AND NVL(D.EXP_YN,'N')='N' AND NOT exists (SELECT 1 FROM WAA_EXP_TBL_RULE EXR WHERE EXR.REG_TYP IN ('C', 'U') AND EXR.DB_SCH_ID = A.DB_SCH_ID AND EXR.DB_CONN_TRG_ID = B.DB_CONN_TRG_ID AND A.DBC_TBL_NM LIKE (CASE WHEN (EXR.RELATION = 'F') THEN EXR.EXC_STND_RULE || '_' || '%' WHEN (EXR.RELATION = 'B') THEN '%' || '_' || EXR.EXC_STND_RULE END) )
AND C.DB_CONN_TRG_ID = #{dbConnTrgId, jdbcType=VARCHAR}
AND A.DBC_TBL_NM = #{dbcTblNm, jdbcType=VARCHAR}

SELECT F.RULE_REL_ID , C.DB_CONN_TRG_PNM , C.DB_CONN_TRG_ID , B.DB_SCH_ID , B.DB_SCH_PNM , A.DBC_TBL_NM , A.DBC_TBL_KOR_NM , E.DBC_COL_NM , E.DBC_COL_KOR_NM , F.VRFC_ID , F.VRFC_NM , F.CD_CLS_ID , F.RULE_REL_ID AS SHD_JOB_ID , 'CR' AS SHD_KND_CD , (F.VRFC_NM||' '|| B.DB_SCH_PNM||'.'|| A.DBC_TBL_NM|| '.'|| E.DBC_COL_NM) AS ETC_JOB_NM , (F.VRFC_NM||' '|| B.DB_SCH_PNM||'.'|| A.DBC_TBL_NM|| '.'|| E.DBC_COL_NM) AS SHD_JOB_NM , PM.PRF_ID , R.ANA_STR_DTM , CASE WHEN E.DATA_LEN IS NULL THEN E.DATA_TYPE ELSE (CASE WHEN E.DATA_PNT IS NOT NULL THEN (E.DATA_TYPE||'('||E.DATA_LEN||', '||E.DATA_PNT||')' ) ELSE (E.DATA_TYPE||'('||E.DATA_LEN||')') END ) END AS DATA_TYPE FROM WAT_DBC_TBL A INNER JOIN WAA_DB_SCH B ON B.EXP_DTM = TO_DATE( '99991231', 'YYYYMMDD') AND B.DB_SCH_ID = A.DB_SCH_ID INNER JOIN WAA_DB_CONN_TRG C ON C.EXP_DTM = TO_DATE( '99991231', 'YYYYMMDD') AND C.DB_CONN_TRG_ID = B.DB_CONN_TRG_ID LEFT JOIN WAA_EXP_TBL D ON D.DB_SCH_ID = A.DB_SCH_ID AND D.DBC_TBL_NM = A.DBC_TBL_NM INNER JOIN WAT_DBC_COL E ON E.DB_SCH_ID = A.DB_SCH_ID AND E.DBC_TBL_NM = A.DBC_TBL_NM LEFT JOIN WAA_COL_RULE_REL F ON F.DB_SCH_ID = A.DB_SCH_ID AND F.DBC_TBL_NM = A.DBC_TBL_NM AND F.DBC_COL_NM = E.DBC_COL_NM LEFT JOIN WAA_VRFC_RULE G ON G.VRFC_ID = F.VRFC_ID AND G.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND G.REG_TYP_CD IN ('C', 'U') LEFT JOIN (SELECT M.DB_CONN_TRG_ID, M.DB_SCH_ID, M.DBC_TBL_NM, M.OBJ_NM, M.PRF_ID FROM WAM_PRF_MSTR M WHERE M.REG_TYP_CD IN ('C','U') AND M.PRF_KND_CD LIKE CONCAT('PC01','%')
AND M.DB_CONN_TRG_ID = #{dbConnTrgId, jdbcType=VARCHAR}
) PM ON C.DB_CONN_TRG_ID = PM.DB_CONN_TRG_ID AND B.DB_SCH_ID = PM.DB_SCH_ID AND A.DBC_TBL_NM = PM.DBC_TBL_NM AND E.DBC_COL_NM = PM.OBJ_NM LEFT OUTER JOIN ( ) R ON R.PRF_ID = PM.PRF_ID WHERE 1 = 1 AND NVL(D.EXP_YN,'N') = 'N'
AND C.DB_CONN_TRG_ID = #{dbConnTrgId, jdbcType=VARCHAR}
AND B.DB_SCH_ID = #{dbSchId, jdbcType=VARCHAR}
AND (A.DBC_TBL_NM LIKE ('%' || #{dbcTblNm, jdbcType=VARCHAR} || '%') OR A.DBC_TBL_NM LIKE UPPER(('%' || #{dbcTblNm, jdbcType=VARCHAR} || '%')))
AND (E.DBC_COL_NM LIKE ('%' || #{dbcColNm, jdbcType=VARCHAR} || '%') OR E.DBC_COL_NM LIKE UPPER(('%' || #{dbcColNm, jdbcType=VARCHAR} || '%')))
AND E.DBC_COL_KOR_NM LIKE ('%' || #{dbcColKorNm, jdbcType=VARCHAR} || '%')
AND F.RULE_REL_ID IS NOT NULL AND F.VRFC_ID IS NOT NULL
AND F.RULE_REL_ID IS NULL
AND G.VRFC_TYP = #{vrfcTyp, jdbcType=VARCHAR}
ORDER BY A.DB_SCH_ID , A.DBC_TBL_NM , E.ORD

SELECT F.RULE_REL_ID AS SHD_JOB_ID , C.DB_CONN_TRG_PNM , C.DB_CONN_TRG_ID , B.DB_SCH_ID , B.DB_SCH_PNM , A.DBC_TBL_NM , A.DBC_TBL_KOR_NM , E.DBC_COL_NM , E.DBC_COL_KOR_NM , F.VRFC_ID , G.VRFC_NM , ('[' || G.VRFC_NM,']' || A.DBC_TBL_NM || '.' || E.DBC_COL_NM) AS SHD_JOB_NM , ('[' || G.VRFC_NM,']' || A.DBC_TBL_NM || '.' || E.DBC_COL_NM) AS ETC_JOB_NM , 'EX' AS ETC_JOB_KND_CD , 'EX' AS SHD_JOB_KND_CD , 'CR' AS SHD_KND_CD FROM WAT_DBC_TBL A INNER JOIN WAA_DB_SCH B ON B.EXP_DTM = TO_DATE( '99991231', 'YYYYMMDD') AND B.DB_SCH_ID = A.DB_SCH_ID INNER JOIN WAA_DB_CONN_TRG C ON C.EXP_DTM = TO_DATE( '99991231', 'YYYYMMDD') AND C.DB_CONN_TRG_ID = B.DB_CONN_TRG_ID LEFT JOIN WAA_EXP_TBL D ON D.DB_SCH_ID = A.DB_SCH_ID AND D.DBC_TBL_NM = A.DBC_TBL_NM INNER JOIN WAT_DBC_COL E ON E.DB_SCH_ID = A.DB_SCH_ID AND E.DBC_TBL_NM = A.DBC_TBL_NM INNER JOIN WAA_COL_RULE_REL F ON F.DB_SCH_ID = A.DB_SCH_ID AND F.DBC_TBL_NM = A.DBC_TBL_NM AND F.DBC_COL_NM = E.DBC_COL_NM INNER JOIN WAA_VRFC_RULE G ON G.VRFC_ID = F.VRFC_ID AND G.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND G.REG_TYP_CD IN ('C', 'U') AND G.VRFC_TYP != 'CD' WHERE 1 = 1 AND NVL(D.EXP_YN,'N') = 'N' AND NOT exists (SELECT 1 FROM WAA_EXP_TBL_RULE EXR WHERE EXR.REG_TYP IN ('C', 'U') AND EXR.DB_SCH_ID = B.DB_SCH_ID AND EXR.DB_CONN_TRG_ID = C.DB_CONN_TRG_ID AND A.DBC_TBL_NM LIKE (CASE WHEN (EXR.RELATION = 'F') THEN EXR.EXC_STND_RULE || '_' || '%' WHEN (EXR.RELATION = 'B') THEN '%' || '_' || EXR.EXC_STND_RULE END) )
AND C.DB_CONN_TRG_ID = #{dbConnTrgId, jdbcType=VARCHAR}
AND C.DB_CONN_TRG_PNM LIKE #{dbConnTrgPnm, jdbcType=VARCHAR}
AND B.DB_SCH_ID = #{dbSchId, jdbcType=VARCHAR}
AND C.DB_CONN_TRG_LNM LIKE ('%' || #{dbConnTrgLnm || jdbcType=VARCHAR} ||'%')
AND A.DBC_TBL_NM = #{dbcTblNm, jdbcType=VARCHAR}
AND ('[' || G.VRFC_NM || ']' || A.DBC_TBL_NM || '.' || E.DBC_COL_NM) LIKE ( '%' || #{shdJobNm,jdbcType=VARCHAR} || '%')
AND G.VRFC_TYP LIKE ( '%' || #{vrfcTyp,jdbcType=VARCHAR} || '%')
UNION ALL SELECT F.RULE_REL_ID AS SHD_JOB_ID , C.DB_CONN_TRG_PNM , C.DB_CONN_TRG_ID , B.DB_SCH_ID , B.DB_SCH_PNM , A.DBC_TBL_NM , A.DBC_TBL_KOR_NM , E.DBC_COL_NM , E.DBC_COL_KOR_NM , F.VRFC_ID , F.VRFC_NM , ('['||F.VRFC_NM,']'|| A.DBC_TBL_NM|| '.'|| E.DBC_COL_NM) AS SHD_JOB_NM , ('['||F.VRFC_NM,']'|| A.DBC_TBL_NM|| '.'|| E.DBC_COL_NM) AS ETC_JOB_NM , 'EX' AS ETC_JOB_KND_CD , 'EX' AS SHD_JOB_KND_CD , 'CR' AS SHD_KND_CD FROM WAT_DBC_TBL A INNER JOIN WAA_DB_SCH B ON B.EXP_DTM = TO_DATE( '99991231', 'YYYYMMDD') AND B.DB_SCH_ID = A.DB_SCH_ID INNER JOIN WAA_DB_CONN_TRG C ON C.EXP_DTM = TO_DATE( '99991231', 'YYYYMMDD') AND C.DB_CONN_TRG_ID = B.DB_CONN_TRG_ID LEFT JOIN WAA_EXP_TBL D ON D.DB_SCH_ID = A.DB_SCH_ID AND D.DBC_TBL_NM = A.DBC_TBL_NM INNER JOIN WAT_DBC_COL E ON E.DB_SCH_ID = A.DB_SCH_ID AND E.DBC_TBL_NM = A.DBC_TBL_NM INNER JOIN WAA_COL_RULE_REL F ON F.DB_SCH_ID = A.DB_SCH_ID AND F.DBC_TBL_NM = A.DBC_TBL_NM AND F.DBC_COL_NM = E.DBC_COL_NM INNER JOIN WAA_CD_RULE G ON G.CD_RULE_ID = F.VRFC_ID WHERE 1 = 1 AND NVL(D.EXP_YN,'N') = 'N' AND NOT exists (SELECT 1 FROM WAA_EXP_TBL_RULE EXR WHERE EXR.REG_TYP IN ('C', 'U') AND EXR.DB_SCH_ID = B.DB_SCH_ID AND EXR.DB_CONN_TRG_ID = C.DB_CONN_TRG_ID AND A.DBC_TBL_NM LIKE (CASE WHEN (EXR.RELATION = 'F') THEN EXR.EXC_STND_RULE || '_' || '%' WHEN (EXR.RELATION = 'B') THEN '%' || '_' || EXR.EXC_STND_RULE END) )
AND C.DB_CONN_TRG_ID = #{dbConnTrgId, jdbcType=VARCHAR}
AND C.DB_CONN_TRG_PNM LIKE #{dbConnTrgPnm, jdbcType=VARCHAR}
AND B.DB_SCH_ID = #{dbSchId, jdbcType=VARCHAR}
AND C.DB_CONN_TRG_LNM LIKE ('%'||#{dbConnTrgLnm, jdbcType=VARCHAR}||'%')
AND A.DBC_TBL_NM = #{dbcTblNm, jdbcType=VARCHAR}
AND ('['||G.VRFC_NM,']'|| A.DBC_TBL_NM|| '.'|| E.DBC_COL_NM) LIKE ( '%' || #{shdJobNm,jdbcType=VARCHAR} || '%')
AND 'CD' LIKE ( '%' || #{vrfcTyp,jdbcType=VARCHAR} || '%')
UNION ALL SELECT A.PRF_ID AS SHD_JOB_ID , C.DB_CONN_TRG_PNM , C.DB_CONN_TRG_ID , B.DB_SCH_ID , B.DB_SCH_PNM , A.DBC_TBL_NM , NULL AS DBC_TBL_KOR_NM , '' AS DBC_COL_NM , '' AS DBC_COL_KOR_NM , '' AS VRFC_ID , '' AS VRFC_NM , ('[참조무결성]'|| B.DB_SCH_PNM|| '.'|| A.DBC_TBL_NM) AS SHD_JOB_NM , ('[참조무결성]'|| B.DB_SCH_PNM|| '.'|| A.DBC_TBL_NM) AS ETC_JOB_NM , 'EX' AS ETC_JOB_KND_CD , 'EX' AS SHD_JOB_KND_CD , 'CR' AS SHD_KND_CD FROM WAM_PRF_MSTR A INNER JOIN WAA_DB_SCH B ON B.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND B.DB_SCH_ID = A.DB_SCH_ID INNER JOIN WAA_DB_CONN_TRG C ON C.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND C.DB_CONN_TRG_ID = B.DB_CONN_TRG_ID WHERE 1 = 1 AND A.PRF_KND_CD = 'PT01' AND NOT exists (SELECT 1 FROM WAA_EXP_TBL_RULE EXR WHERE EXR.REG_TYP IN ('C', 'U') AND EXR.DB_SCH_ID = B.DB_SCH_ID AND EXR.DB_CONN_TRG_ID = C.DB_CONN_TRG_ID AND A.DBC_TBL_NM LIKE (CASE WHEN (EXR.RELATION = 'F') THEN EXR.EXC_STND_RULE || '_' || '%' WHEN (EXR.RELATION = 'B') THEN '%' || '_' || EXR.EXC_STND_RULE END) )
AND C.DB_CONN_TRG_ID = #{dbConnTrgId, jdbcType=VARCHAR}
AND C.DB_CONN_TRG_PNM LIKE #{dbConnTrgPnm, jdbcType=VARCHAR}
AND B.DB_SCH_ID = #{dbSchId, jdbcType=VARCHAR}
AND C.DB_CONN_TRG_LNM LIKE ('%'||#{dbConnTrgLnm, jdbcType=VARCHAR}||'%')
AND A.DBC_TBL_NM = #{dbcTblNm, jdbcType=VARCHAR}
AND A.PRF_NM LIKE ( '%' || #{shdJobNm,jdbcType=VARCHAR} || '%')
AND A.PRF_KND_CD = #{vrfcTyp,jdbcType=VARCHAR}
ORDER BY DB_SCH_ID , DBC_TBL_NM
