mapper.select:
SELECT X.DMN_ID , X.DB_CONN_TRG_ID , X.DB_CONN_TRG_PNM , X.DB_SCH_ID , X.DB_SCH_PNM , X.UPP_DMNG_NM , X.DMNG_NM , X.DMN_NM , (CASE WHEN C.DMN_NM IS NULL THEN '정상' ELSE '도메인명 중복' END) AS VRF_RMK
, X.DATA_TYPE , X.DATA_LEN , X.DATA_SCAL
, X.DIC_ORGN , X.REG_DTM , X.REG_USER_ID , CASE WHEN C.DMN_NM IS NULL THEN '#000000' ELSE '#FF0000' END AS FontColor FROM ( SELECT GD.DMN_ID , GD.DB_CONN_TRG_ID , WDCT.DB_CONN_TRG_PNM , GD.DB_SCH_ID , WDS.DB_SCH_PNM , GD.UPP_DMNG_NM , GD.DMNG_NM , GD.DMN_NM
, GD.DATA_TYPE , GD.DATA_LEN , GD.DATA_SCAL
, GD.DIC_ORGN , GD.REG_DTM , GD.REG_USER_ID FROM GOV_DMN GD LEFT OUTER JOIN WAA_DB_CONN_TRG WDCT ON GD.DB_CONN_TRG_ID= WDCT.DB_CONN_TRG_ID AND WDCT.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_DB_SCH WDS ON GD.DB_SCH_ID = WDS.DB_SCH_ID AND WDS.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
GD.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND GD.REG_TYP_CD IN ('C','U')
AND GD.DMNG_NM = #{dmngNm,jdbcType=VARCHAR}
AND (GD.DMN_NM LIKE '%' || #{dmnNm,jdbcType=VARCHAR} || '%')
AND GD.DATA_TYPE = #{dataType,jdbcType=VARCHAR}
AND WDCT.DB_CONN_TRG_ID = #{dbConnTrgId,jdbcType=VARCHAR}
AND WDS.DB_SCH_ID = #{dbSchId,jdbcType=VARCHAR}
) X LEFT OUTER JOIN ( SELECT B.DB_SCH_ID, B.DMN_NM, COUNT(*) FROM GOV_DMN B WHERE B.REG_TYP_CD IN ('C','U') AND B.EXP_DTM = TO_DATE('99991231','YYYYMMDD') GROUP BY B.DB_SCH_ID, B.DMN_NM HAVING COUNT(*) > 1 ) C ON X.DB_SCH_ID = C.DB_SCH_ID AND X.DMN_NM = C.DMN_NM

mapper.update:
UPDATE GOV_DMN SET VRF_RMK = NULL
VRF_RMK IS NOT NULL
AND DMN_ID = #{dmnId, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM GOV_DMN
WHERE 1=1
	AND DMN_ID =#{dmnId,jdbcType=VARCHAR}
