 WHERE RQST_NO  = #{rqstNo}
   AND RQST_SNO = #{rqstSno}

mapper.select:
     SELECT CASE WHEN count(*) > 0 THEN 0 ELSE 1 END 
 	 FROM WAQ_APP_PRGM
	 WHERE RQST_NO = #{rqstNo}

mapper.update:
   --신규 적재 대상 ID 업데이트
   UPDATE WAQ_APP_PRGM 
      SET APP_PRGM_ID = #{appPrgmId,jdbcType=VARCHAR}
	  WHERE RQST_NO    = #{rqstNo,jdbcType=VARCHAR}
      AND RQST_SNO   = #{rqstSno,jdbcType=DECIMAL}

mapper.insert:
--WAM 추가 (WAQ 내용을 추가, REG_TYP_CD 가 신규,변경일 경우) INSERT INTO WAM_APP_PRGM ( APP_PRGM_ID
) SELECT APP_PRGM_ID
FROM WAQ_APP_PRGM A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

--WAH이력 적재... INSERT INTO WAH_APP_PRGM ( APP_PRGM_ID , EXP_DTM , STR_DTM
) SELECT A.APP_PRGM_ID AS APP_PRGM_ID , TO_DATE( '9999-12-31', 'YYYY-MM-DD') AS EXP_DTM , SYSDATE AS STR_DTM
FROM WAQ_APP_PRGM A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1'

