mapper.select:
select 

,

from WAQ_DDL_GRT
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_DDL_GRT
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

DELETE FROM WAQ_DDL_GRT
 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
   AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
	-- DDL 오브젝트 요청서 상세조회
	  SELECT A.*
      FROM WAQ_DDL_GRT A 
     WHERE A.RQST_NO  = #{rqstNo,jdbcType=VARCHAR}
       AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
DELETE FROM WAQ_DDL_GRT
 WHERE RQST_NO      = #{rqstNo}
   AND RQST_SNO     = #{rqstSno}

mapper.update:
-- 각종 ID 업데이트, 권한 업데이트 UPDATE WAQ_DDL_GRT A
SET (A.GRTED_DB_ID, A.GRTED_SCH_ID) = ( SELECT MAX(D.DB_CONN_TRG_ID), MAX(C.ROLE_ID)
FROM WAA_DB_ROLE C INNER JOIN WAA_DB_CONN_TRG D ON D.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND D.REG_TYP_CD IN ('C', 'U') AND D.DB_CONN_TRG_ID = C.DB_CONN_TRG_ID WHERE 1 = 1 AND D.DB_CONN_TRG_PNM = A.GRTED_DB_PNM AND C.ROLE_PNM = A.GRTED_SCH_PNM AND C.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND C.REG_TYP_CD IN ('C', 'U') ) , A.VRF_CD = '4' WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}

   --신규 적재 대상 ID 업데이트
  UPDATE WAQ_DDL_GRT 
     SET DDL_GRT_ID = #{ddlGrtId,jdbcType=VARCHAR}
	 WHERE RQST_NO      = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO     = #{rqstSno,jdbcType=DECIMAL}

mapper.insert:
--WAM 추가 (WAQ 내용을 추가, REG_TYP_CD 가 신규,변경일 경우) INSERT INTO WAM_DDL_GRT (DDL_GRT_ID
) SELECT DDL_GRT_ID
FROM WAQ_DDL_GRT A WHERE A.RQST_NO = #{rqstNo} AND A.VRF_CD = '1' AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

--WAH이력 적재... INSERT INTO WAH_DDL_GRT ( DDL_GRT_ID , EXP_DTM , STR_DTM
) SELECT A.DDL_GRT_ID AS DDL_GRT_ID , TO_DATE( '9999-12-31', 'YYYY-MM-DD') AS EXP_DTM , SYSDATE AS STR_DTM
FROM WAQ_DDL_GRT A WHERE A.RQST_NO = #{rqstNo} AND A.VRF_CD = '1' AND A.RVW_STS_CD = '1'

mapper.update:
	--DDL 스크립트 업데이트...
	UPDATE WAQ_DDL_GRT
	   SET SCRT_INFO = #{scrtInfo,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
