mapper.select:
select 

from WAQ_DDL_TSF_REL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}
  and DDL_REL_PNM = #{ddlRelPnm,jdbcType=VARCHAR}
  and PA_ENTY_PNM = #{paEntyPnm,jdbcType=VARCHAR}
  and PA_ATTR_PNM = #{paAttrPnm,jdbcType=VARCHAR}
  and CH_ENTY_PNM = #{chEntyPnm,jdbcType=VARCHAR}
  and CH_ATTR_PNM = #{chAttrPnm,jdbcType=VARCHAR}

mapper.delete:
delete from WAQ_DDL_TSF_REL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}
  and DDL_REL_PNM = #{ddlRelPnm,jdbcType=VARCHAR}
  and PA_ENTY_PNM = #{paEntyPnm,jdbcType=VARCHAR}
  and PA_ATTR_PNM = #{paAttrPnm,jdbcType=VARCHAR}
  and CH_ENTY_PNM = #{chEntyPnm,jdbcType=VARCHAR}
  and CH_ATTR_PNM = #{chAttrPnm,jdbcType=VARCHAR}

DELETE FROM WAQ_DDL_TSF_REL
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
--등록유형 업데이트 UPDATE WAQ_DDL_TSF_REL A SET A.REG_TYP_CD = (select case when A.RQST_DCD = 'DD' then 'D' else case when MAX(B.DDL_REL_ID) IS NULL then 'C' else 'U' end end from WAM_DDL_TSF_REL B inner join WAM_DDL_TSF_TBL C on C.DDL_TBL_ID = B.CH_ENTY_ID and C.REG_TYP_CD in ('C','U') INNER JOIN WAQ_DDL_TSF_TBL D ON C.TGT_DB_SCH_ID = D.TGT_DB_SCH_ID AND C.DDL_TBL_PNM = D.DDL_TBL_PNM
AND D.RQST_NO = #{rqstNo} where B.REG_TYP_CD in ('C','U') and B.DDL_REL_PNM = A.DDL_REL_PNM
and D.RQST_NO = A.RQST_NO AND D.RQST_SNO = A.RQST_SNO ) ,A.VRF_CD = '4' WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}

mapper.insert:
--검증쿼리 : 변경사항 없을경우 (DTR00) AND A.REG_TYP_CD = 'U' AND EXISTS (SELECT 1 FROM WAM_DDL_TSF_TBL D JOIN WAQ_DDL_TSF_TBL F ON F.DDL_TBL_PNM = D.DDL_TBL_PNM AND D.SRC_DB_SCH_ID = F.SRC_DB_SCH_ID AND D.TGT_DB_SCH_ID = F.TGT_DB_SCH_ID
JOIN WAM_DDL_TSF_REL E ON D.DDL_TBL_ID = E.CH_ENTY_ID WHERE F.RQST_NO = A.RQST_NO AND F.RQST_SNO = A.RQST_SNO AND A.DDL_REL_PNM = E.DDL_REL_PNM AND A.DDL_REL_LNM = E.DDL_REL_LNM and NVL(A.REL_TYP_CD , '_') = NVL(E.REL_TYP_CD , '_') and NVL(A.CRD_TYP_CD , '_') = NVL(E.CRD_TYP_CD , '_') and NVL(A.PA_OPT_TYP_CD , '_') = NVL(E.PA_OPT_TYP_CD , '_') and NVL(A.PA_SUBJ_ID , '_') = NVL(E.PA_SUBJ_ID, '_') and NVL(A.PA_ENTY_PNM , '_') = NVL((SELECT DDL_TBL_PNM FROM WAM_DDL_TBL WHERE REG_TYP_CD IN ('C', 'U') AND DDL_TBL_ID = E.PA_ENTY_ID) , '_') and NVL(A.PA_ATTR_PNM , '_') = NVL((SELECT DDL_COL_PNM FROM WAM_DDL_COL WHERE REG_TYP_CD IN ('C', 'U') AND DDL_COL_ID = E.PA_ATTR_ID) , '_') and NVL(A.CH_SUBJ_ID , '_') = NVL(E.CH_SUBJ_ID, '_') and NVL(A.CH_ENTY_PNM , '_') = NVL((SELECT DDL_TBL_PNM FROM WAM_DDL_TBL WHERE REG_TYP_CD IN ('C', 'U') AND DDL_TBL_ID = E.CH_ENTY_ID) , '_') and NVL(A.CH_ATTR_PNM , '_') = NVL((SELECT DDL_COL_PNM FROM WAM_DDL_COL WHERE REG_TYP_CD IN ('C', 'U') AND DDL_COL_ID = E.CH_ATTR_ID) , '_')
)
