SELECT
A.LDM_ATTR_ID , A.LDM_ATTR_PNM , A.LDM_ATTR_LNM , A.LDM_ENTY_ID , A.SDITM_ID , C.SDITM_LNM , C.SDITM_PNM , D.DMN_ID , D.DMN_LNM , D.DMN_PNM , E.INFOTP_ID , E.INFOTP_LNM , A.COL_ORD , CASE WHEN A.DATA_LEN IS NULL THEN A.DATA_TYPE ELSE (CASE WHEN A.DATA_SCAL IS NOT NULL THEN A.DATA_TYPE || '(' || A.DATA_LEN ||','|| A.DATA_SCAL ||')' ELSE A.DATA_TYPE || '(' || A.DATA_LEN ||')' END ) END AS DATA_TYPE , NVL(A.PK_YN, 'N') AS PK_YN , A.PK_ORD , NVL(A.NONUL_YN, 'N') AS NONUL_YN , A.DEFLT_VAL , A.ENC_YN , A.OBJ_DESCN , A.OBJ_VERS , A.REG_TYP_CD , A.FRS_RQST_DTM , A.FRS_RQST_USER_ID , A.RQST_DTM , A.RQST_USER_ID , A.APRV_DTM , A.APRV_USER_ID , B.LDM_ENTY_LNM , B.SUBJ_ID , SUBJ.SUBJ_LNM , SUBJ.SUBJ_PNM , SUBJ.FULL_PATH , B.STD_APL_YN , GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM , GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM , GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM FROM WAM_LDM_ENTY B INNER JOIN WAM_LDM_ATTR A ON B.LDM_ENTY_ID = A.LDM_ENTY_ID AND A.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAM_SDITM C ON A.SDITM_ID = C.SDITM_ID AND C.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAM_DMN D ON C.DMN_ID = D.DMN_ID AND D.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAA_INFO_TYPE E ON D.INFOTP_ID = E.INFOTP_ID AND E.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_SUBJ SUBJ ON B.SUBJ_ID = SUBJ.SUBJ_ID AND SUBJ.EXP_DTM = TO_DATE('99991231', 'YYYYMMDD') AND SUBJ.REG_TYP_CD IN ('C', 'U')
AND B.REG_TYP_CD IN ('C', 'U')
AND B.LDM_ENTY_ID = #{ldmEntyId,jdbcType=VARCHAR} --컬럼 매핑정의서 조회시 특정컬럼명의 컬럼상세정보얻기위한 조건. AND A.LDM_ATTR_ID = #{tgtColId,jdbcType=VARCHAR}
--컬럼 조회를 위한 추가 조건 20150922 이상익. AND (B.LDM_ENTY_LNM LIKE '%'||#{ldmEntyLnm,jdbcType=VARCHAR}||'%' OR B.LDM_ENTY_PNM LIKE '%'||#{ldmEntyLnm,jdbcType=VARCHAR}||'%')
--컬럼 조회를 위한 추가 조건 20150922 이상익. AND (A.LDM_ATTR_LNM LIKE '%'||#{ldmAttrLnm,jdbcType=VARCHAR}||'%' OR A.LDM_ATTR_PNM LIKE '%'||#{ldmAttrLnm,jdbcType=VARCHAR}||'%')
----컬럼 조회를 위한 추가 조건 20150922 이상익. AND B.SUBJ_ID = #{subjId,jdbcType=VARCHAR}
----컬럼 조회를 위한 추가 조건 20150922 이상익. AND SUBJ.FULL_PATH LIKE '%'||#{subjLnm,jdbcType=VARCHAR}||'%'
----컬럼 조회를 위한 추가 조건 20150922 이상익. AND B.STD_APL_YN = #{stdAplYn,jdbcType=VARCHAR}
ORDER BY B.LDM_ENTY_LNM, A.COL_ORD

SELECT
A.LDM_ATTR_ID , A.LDM_ATTR_PNM , A.LDM_ATTR_LNM , A.LDM_ENTY_ID , A.SDITM_ID , C.SDITM_LNM , C.SDITM_PNM , D.DMN_ID , D.DMN_LNM , D.DMN_PNM , E.INFOTP_ID , E.INFOTP_LNM , E.DATA_TYPE AS SDITM_DATA_TYPE , E.DATA_LEN AS SDITM_DATA_LEN , E.DATA_SCAL AS SDITM_DATA_SCAL , A.COL_ORD
, A.DATA_TYPE , A.DATA_LEN , A.DATA_SCAL , NVL(A.PK_YN, 'N') AS PK_YN , A.PK_ORD , NVL(A.NONUL_YN, 'N') AS NONUL_YN , A.DEFLT_VAL , A.ENC_YN , A.OBJ_DESCN , A.OBJ_VERS , A.REG_TYP_CD , A.FRS_RQST_DTM , A.FRS_RQST_USER_ID , A.RQST_DTM , A.RQST_USER_ID , A.APRV_DTM , A.APRV_USER_ID , B.LDM_ENTY_LNM , B.LDM_ENTY_PNM , B.SUBJ_ID , SUBJ.SUBJ_LNM , SUBJ.SUBJ_PNM , SUBJ.FULL_PATH , B.STD_APL_YN , GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM , GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM , GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM ,CASE WHEN NVL(A.LDM_ATTR_LNM,'X') != RTRIM(C.SDITM_LNM,'_0123456789') THEN 'N' ELSE CASE WHEN NVL(C.SDITM_ID,'X') = 'X' AND NVL(A.LDM_ATTR_PNM,'X') = RTRIM(C.SDITM_PNM,'_0123456789') AND NVL(A.DATA_TYPE,'X') = NVL(E.DATA_TYPE,'X') AND NVL(A.DATA_LEN,'0') = NVL(E.DATA_LEN,'0') AND NVL(A.DATA_SCAL,'0') = NVL(E.DATA_SCAL,'0') THEN 'Y' ELSE 'E' END END AS ITEM_STATUS, CASE WHEN NVL(C.SDITM_ID,'X') != 'X' THEN CASE WHEN NVL(A.SDITM_ID,'X') != NVL(C.SDITM_ID,'X') THEN '항목정보 또는 ID 다름' ELSE '정상' END ELSE '미존재' END AS ERR_ITEM_ID_YN ,CASE WHEN NVL(C.SDITM_PNM,'X') != 'X' THEN CASE WHEN NVL(RTRIM(A.LDM_ATTR_PNM,'_0123456789'),'X') != C.SDITM_PNM THEN '항목영문명과다름' ELSE '정상' END ELSE '미존재' END AS ERR_COL_PNM_YN ,CASE WHEN NVL(RTRIM(A.LDM_ATTR_LNM,'_0123456789'),'X') = C.SDITM_LNM THEN CASE WHEN NVL(A.DATA_TYPE,'X') != NVL(E.DATA_TYPE,'X') THEN '데이터타입다름(COL:'|| A.DATA_TYPE ||',ITEM:' || E.DATA_TYPE ||')' ELSE '정상' END ELSE '' END AS ERR_DATA_TYPE_YN ,CASE WHEN NVL(RTRIM(A.LDM_ATTR_LNM,'_0123456789'),'X') = C.SDITM_LNM THEN CASE WHEN NVL(A.DATA_LEN,'0') != NVL(E.DATA_LEN,'0') THEN '데이터길이다름(COL:'|| A.DATA_LEN ||',ITEM:' || E.DATA_LEN ||')' ELSE '정상' END ELSE '' END AS ERR_DATA_LENGTH_YN ,CASE WHEN NVL(RTRIM(A.LDM_ATTR_LNM,'_0123456789'),'X') = C.SDITM_LNM THEN CASE WHEN NVL(A.DATA_SCAL,'0') != NVL(E.DATA_SCAL,'0') THEN '소수점다름(COL:'|| A.DATA_SCAL ||',ITEM:' || E.DATA_SCAL ||')' ELSE '정상' END ELSE '' END AS ERR_DATA_SCALE_YN FROM WAM_LDM_ENTY B INNER JOIN WAM_LDM_ATTR A ON B.LDM_ENTY_ID = A.LDM_ENTY_ID AND A.REG_TYP_CD IN ('C', 'U') LEFT JOIN WAM_SDITM C ON C.SDITM_LNM = RTRIM(A.LDM_ATTR_LNM,'_0123456789') LEFT JOIN WAM_DMN D ON D.DMN_ID = C.DMN_ID AND D.REG_TYP_CD IN ('C', 'U') LEFT JOIN WAA_INFO_TYPE E ON E.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND E.INFOTP_ID = D.INFOTP_ID LEFT JOIN WAA_SUBJ SUBJ ON SUBJ.EXP_DTM = TO_DATE('99991231', 'YYYYMMDD') AND SUBJ.REG_TYP_CD IN ('C', 'U') AND SUBJ.SUBJ_ID = B.SUBJ_ID
AND B.REG_TYP_CD IN ('C', 'U')
--> AND B.LDM_ENTY_ID = #{ldmEntyId,jdbcType=VARCHAR}
--컬럼 매핑정의서 조회시 특정컬럼명의 컬럼상세정보얻기위한 조건. AND A.LDM_ATTR_ID = #{tgtColId,jdbcType=VARCHAR}
--컬럼 조회를 위한 추가 조건 20150922 이상익. AND (B.LDM_ENTY_LNM LIKE '%'||#{ldmEntyLnm,jdbcType=VARCHAR}||'%' OR B.LDM_ENTY_PNM LIKE '%'||#{ldmEntyLnm,jdbcType=VARCHAR}||'%')
--컬럼 조회를 위한 추가 조건 20150922 이상익. AND (A.LDM_ATTR_LNM LIKE '%'||#{ldmAttrLnm,jdbcType=VARCHAR}||'%' OR A.LDM_ATTR_PNM LIKE '%'||#{ldmAttrLnm,jdbcType=VARCHAR}||'%')
----컬럼 조회를 위한 추가 조건 20150922 이상익. AND B.SUBJ_ID = #{subjId,jdbcType=VARCHAR}
----컬럼 조회를 위한 추가 조건 20150922 이상익. AND SUBJ.FULL_PATH LIKE '%'||#{subjLnm,jdbcType=VARCHAR}||'%'
----컬럼 조회를 위한 추가 조건 20150922 이상익. AND B.STD_APL_YN = #{stdAplYn,jdbcType=VARCHAR}
ORDER BY B.LDM_ENTY_LNM, A.COL_ORD
