mapper.select:
select B.SYS_AREA_ID , B.FULL_PATH , B.SUBJ_ID , B.SUBJ_LNM , A.LDM_ENTY_ID , A.LDM_ENTY_PNM , A.LDM_ENTY_LNM , A.STD_APL_YN , A.PART_TBL_YN , A.DW_TRG_TBL_YN , A.CRG_USER_ID , A.OBJ_DESCN , ( CASE WHEN A.LDM_ENTY_ID IS NOT NULL THEN (CASE WHEN C.LDM_ENTY_ID IS NOT NULL THEN 'U' ELSE 'C' END) END ) AS REG_TYP_CD , E.DB_CONN_TRG_ID , E.DB_CONN_TRG_PNM , D.DB_SCH_ID , D.DB_SCH_PNM , A.APRV_DTM FROM WAM_LDM_ENTY A LEFT JOIN WAA_SUBJ B ON A.SUBJ_ID = B.SUBJ_ID AND B.REG_TYP_CD IN ('C', 'U') AND B.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT JOIN WAM_DDL_TBL C ON C.LDM_ENTY_ID = A.LDM_ENTY_ID AND C.DB_SCH_ID = #{dbSchId,jdbcType=VARCHAR} AND C.DDL_TRG_DCD = 'D' LEFT JOIN WAA_DB_SCH D ON D.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND D.REG_TYP_CD IN ('C', 'U') AND D.DB_SCH_ID = #{dbSchId,jdbcType=VARCHAR} LEFT JOIN WAA_DB_CONN_TRG E ON E.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND E.DB_CONN_TRG_ID = D.DB_CONN_TRG_ID WHERE 1 = 1
AND A.LDM_ENTY_ID = #{ldmEntyId,jdbcType=VARCHAR}
AND (A.LDM_ENTY_PNM LIKE '%'||UPPER(#{ldmEntyLnm,jdbcType=VARCHAR})||'%' OR A.LDM_ENTY_LNM LIKE '%'||#{ldmEntyLnm,jdbcType=VARCHAR}||'%')
AND (B.FULL_PATH LIKE '%'||#{subjLnm,jdbcType=VARCHAR}||'%')
AND B.SYS_AREA_ID = #{sysAreaId,jdbcType=VARCHAR}
AND A.SUBJ_ID = #{subjId,jdbcType=VARCHAR}
AND A.STD_APL_YN = #{stdAplYn,jdbcType=VARCHAR}
ORDER BY A.APRV_DTM DESC , A.FULL_PATH , A.LDM_ENTY_PNM

SELECT B.SYS_AREA_ID , B.FULL_PATH , B.SUBJ_ID , B.SUBJ_LNM , A.LDM_ENTY_ID , E.DB_CONN_TRG_PNM , D.DB_SCH_PNM , C.DDL_TBL_PNM AS LDM_ENTY_PNM , C.DDL_TBL_LNM AS LDM_ENTY_LNM , A.STD_APL_YN , A.PART_TBL_YN , A.DW_TRG_TBL_YN , A.CRG_USER_ID , A.OBJ_DESCN , 'D' AS REG_TYP_CD , 'DD' AS RQST_DCD FROM WAM_DDL_TBL C LEFT JOIN WAH_LDM_ENTY A ON A.LDM_ENTY_ID = C.LDM_ENTY_ID AND A.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT JOIN WAA_SUBJ B ON A.SUBJ_ID = B.SUBJ_ID AND B.REG_TYP_CD IN ('C', 'U') AND B.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT JOIN WAA_DB_SCH D ON D.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND D.REG_TYP_CD IN ('C', 'U') AND D.DB_SCH_ID = C.DB_SCH_ID LEFT JOIN WAA_DB_CONN_TRG E ON E.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND E.REG_TYP_CD IN ('C', 'U') AND E.DB_CONN_TRG_ID = D.DB_CONN_TRG_ID WHERE 1 = 1 AND C.DB_SCH_ID = #{dbSchId,jdbcType=VARCHAR}
AND A.LDM_ENTY_ID = #{ldmEntyId,jdbcType=VARCHAR}
AND (A.LDM_ENTY_PNM LIKE '%'||UPPER(#{ldmEntyLnm,jdbcType=VARCHAR})||'%' OR A.LDM_ENTY_LNM LIKE '%'||#{ldmEntyLnm,jdbcType=VARCHAR}||'%')
AND (B.FULL_PATH LIKE '%'||#{subjLnm,jdbcType=VARCHAR}||'%')
AND B.SYS_AREA_ID = #{sysAreaId,jdbcType=VARCHAR}
AND A.SUBJ_ID = #{subjId,jdbcType=VARCHAR}
AND A.STD_APL_YN = #{stdAplYn,jdbcType=VARCHAR}
