mapper.select:
select 

from WAQ_TBL_MAP
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_TBL_MAP
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

DELETE FROM WAQ_TBL_MAP
 WHERE RQST_NO  = #{rqstNo}
   AND RQST_SNO = #{rqstSno}

mapper.select:
-- 테이블매핑정의서 요청 상세정보 조회
select 

from WAQ_TBL_MAP
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
   --신규 적재 대상 ID 업데이트
   UPDATE WAQ_TBL_MAP SET TBL_MAP_ID = #{tblMapId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

--타겟(TOBE)테이블 주제영역정보 업데이트(엑셀업로드시_변경) UPDATE WAQ_TBL_MAP A SET (A.TGT_SUBJ_ID, A.TGT_TBL_LNM,A.TGT_SUBJ_ALL_PATH ) = (SELECT D.TGT_SUBJ_ID, D.TGT_TBL_LNM,D.TGT_SUBJ_ALL_PATH FROM WAM_TBL_MAP D WHERE A.MAP_DF_ID = D.MAP_DF_ID AND NVL(A.SRC_TBL_PNM, 'X') = NVL(D.SRC_TBL_PNM, 'X')
) WHERE A.RQST_NO = #{rqstNo} AND A.REG_TYP_CD = 'U' AND EXISTS ( SELECT 1 FROM WAM_TBL_MAP D WHERE A.MAP_DF_ID = D.MAP_DF_ID AND NVL(A.SRC_TBL_PNM, 'X') = NVL(D.SRC_TBL_PNM, 'X')
)
