mapper.select:
select 

from WAE_PDM_COL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

SELECT
, CASE WHEN A.VRF_CD = '2' THEN '#FF0000' ELSE CASE WHEN A.VRF_CD = '5' THEN '#0000FF' END END AS FontColor --, CASE WHEN A.VRF_CD = '2' THEN '#FF0000' END AS FontColor , U.USER_NM AS RQST_USER_NM , A.ENC_YN FROM WAE_PDM_COL A JOIN WAQ_PDM_TBL B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO LEFT OUTER JOIN WAA_USER U ON A.RQST_USER_ID = U.USER_ID AND U.REG_TYP_CD IN ('C', 'U') AND U.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') WHERE A.RQST_NO = #{rqstNo}
AND A.RQST_SNO = #{rqstSno}
ORDER BY A.RQST_SNO, A.COL_ORD

mapper.delete:
delete from WAE_PDM_COL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

    DELETE /* WaePdmColMapper.deleteByrqstSno */
	FROM WAE_PDM_COL
    WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}

DELETE FROM WAE_PDM_COL
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
